import React from 'react';
import { TouchableOpacity, StyleSheet, Text, View } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { useCart } from '../../CartContext'; 

export default function CartIcon() {
  const navigation = useNavigation();
  const { cartItems } = useCart(); 

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
  };

  return (
    <>
      <TouchableOpacity
        onPress={() => navigation.navigate('Dashboard')} 
        style={styles.cartButton}
      >
        <View style={styles.cartIconContainer}>
        <Text style={styles.cartIcon}>
            ðŸ›’
            {cartItems.length > 0 && (
              <Text style={styles.cartItemCount}>{cartItems.length}</Text>
            )}
          </Text>
          
        </View>
        </TouchableOpacity>
        {cartItems.length > 0 && (
          <View style={styles.cartItems}>
            {cartItems.map((item, index) => (
              <View key={index} style={styles.cartItem}>
                <Text style={styles.cartItemName}>{item.name}</Text>
                <Text style={styles.cartItemPrice}>{item.price}</Text>
              </View>
            ))}
            <Text style={styles.cartTotal}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
          </View>
        )}
      
    </>
  );
}

const styles = StyleSheet.create({
  backButton: {
    position: 'absolute',
    top: 20,
    left: 20,
    backgroundColor: 'transparent',
  },
  cartButton: {
    position: 'absolute',
    right: 20,
    top: 5,
    backgroundColor: 'transparent',
    alignItems: 'center', // Center-align the cart icon
  },
  cartIconContainer: {
    flexDirection: 'row', // Align cart icon and count horizontally
  },
  cartIcon: {
    fontSize: 24,
    marginTop: 10,
  },
  cartItemCount: {
    position: 'absolute',
    top: 0,
    right: 0,
    backgroundColor: 'red',
    color: 'white',
    borderRadius: 10,
    paddingHorizontal: 4, 
    paddingVertical: 2,
    fontSize: 10,
  },
  cartTotal: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 5,
  },
  cartItems: {
    marginTop: 10,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 5,
  },
  cartItemName: {
    fontSize: 14,
  },
  cartItemPrice: {
    fontSize: 14,
    fontWeight: 'bold',
  },
});
