import React, { useState } from "react";
import {
  Text,
  TextInput,
  View,
  StyleSheet,
  TouchableOpacity,
  SafeAreaView,
  ActivityIndicator,
} from "react-native";
import { StatusBar } from "expo-status-bar";
import * as Animatable from "react-native-animatable";
import { LinearGradient } from "expo-linear-gradient";
import Axios from "axios";
import Logo from "../components/Logo";

export default function LoginScreen({ navigation }) {
  const [state, setState] = useState({
    username: "",
    password: "",
    isValidUser: true,
    isValidPassword: true,
    checkTextInputChange: true,
    loading: false, // Add loading state
    error: "", // Add error state
  });

  const textUsernameChange = (val) => {
    if (val.length >= 4) {
      setState({
        ...state,
        username: val,
        checkTextInputChange: true,
      });
    } else {
      setState({
        ...state,
        username: val,
        checkTextInputChange: false,
      });
    }
  };

  const handlePasswordChange = (val) => {
    if (val.length >= 6) {
      setState({
        ...state,
        password: val,
        isValidPassword: true,
      });
    } else {
      setState({
        ...state,
        password: val,
        isValidPassword: false,
      });
    }
  };

  const handleUser = (val) => {
    if (val.trim().length >= 4) {
      setState({
        ...state,
        isValidUser: true,
      });
    } else {
      setState({
        ...state,
        isValidUser: false,
      });
    }
  };

  const LoginData = () => {
    if (state.username.trim() === "" || state.password.trim() === "") {
      alert("Please enter both email and password.");
      return;
    }

    // Set loading state to true
    setState({ ...state, loading: true, error: "" });

    Axios.post("http://localhost:8000/data/signin", {
      username: state.username,
      password: state.password,
    })
      .then((response) => {
        if (response.data.status === "success") {
          navigation.push("Dashboard", { username: state.username });
        } else {
          // Set error message
          setState({ ...state, error: "Email and password do not match.", loading: false });
        }
      })
      .catch(() => {
        // Set error message
        setState({ ...state, error: "An error occurred during login.", loading: false });
      });
  };

  return (
    <View>
      <SafeAreaView style={{ flex: 1, backgroundColor: "lightgrey" }}>
        <View style={styles.container}>
          <StatusBar style="auto" />
        </View>
      </SafeAreaView>
      <View style={styles.container}>
        <Animatable.View animation="rubberBand" duration={1000}>
          <View style={styles.data}>
            <View style={styles.logoContainer}>
              <Logo style={styles.logo} />
            </View>
            <Text style={styles.applego}>APPLEGO</Text>
            <Text style={styles.userdata}>Username</Text>
            <TextInput
              placeholder="Enter Username"
              style={styles.username}
              onChangeText={(e) => textUsernameChange(e)}
              onEndEditing={(e) => handleUser(e.nativeEvent.text)}
            />
            {state.isValidUser ? null : (
              <Animatable.View>
                <Text style={styles.error}>
                  Username should be 4 characters long
                </Text>
              </Animatable.View>
            )}

            <Text style={styles.userdata}>Password</Text>
            <TextInput
              secureTextEntry
              placeholder="Password"
              style={styles.username}
              onChangeText={(e) => handlePasswordChange(e)}
            />

            {state.isValidPassword ? null : (
              <Animatable.View animation="fadeInLeft" duration={500}>
                <Text style={styles.error}>
                  Password should be 6 characters long
                </Text>
              </Animatable.View>
            )}

            <TouchableOpacity
              onPress={() => navigation.navigate("ResetPasswordScreen")}
            >
              <Text
                style={{
                  color: "black",
                  fontSize: 14,
                  textAlign: "center",
                  alignSelf: "center",
                  paddingTop: 10,
                }}
              >
                Forgot Password?
              </Text>
            </TouchableOpacity>

            {state.error ? (
              <Text style={styles.error}>{state.error}</Text>
            ) : null}

            {state.loading ? (
              <ActivityIndicator size="large" color="#f72855" />
            ) : (
              <LinearGradient
                colors={["#f72855", "#f72855"]}
                style={styles.LoginUser}
              >
                <Text
                  style={[
                    styles.Button1,
                    { fontWeight: "bold", color: "white" },
                  ]}
                  onPress={() => LoginData()}
                >
                  Login
                </Text>
              </LinearGradient>
            )}
            <Text
              style={styles.signup}
              onPress={() => navigation.navigate("RegisterScreen")}
            >
              Don't have an account? Sign Up{" "}
            </Text>
          </View>
        </Animatable.View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    height: "100%",
    width: "100%",
    backgroundColor: "#f55164",
  },
  data: {
    marginTop: 60,
    backgroundColor: "rgba(255, 190, 123, 0.6)",
    height: 700,
    paddingTop: 80,
    alignContent: "center",
    alignSelf: "center",
    width: "95%",
    borderRadius: 20,
  },
  username: {
    fontFamily: "sans-serif",
    alignSelf: "center",
    padding: 15,
    textAlign: "center",
    borderBottomColor: "rgba(100,50,40,0.2)",
    borderBottomWidth: 1,
    width: "80%",
    backgroundColor: "#d8dbd7",
    justifyContent: "center",
    fontSize: 20,
    borderRadius: 10,
  },
  // ... other styles
});
