import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated, TouchableWithoutFeedback } from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler'; // Import PanGestureHandler
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';
import McDonaldsMenu from '../components/fastfood/McDonaldsMenu';

export default function Dashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));

  const handlePress = () => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      navigation.navigate('McDonaldsMenu');
    });
  };

  // Handle swipe action
  const handleSwipeRight = () => {
    // Add your logic here for what should happen when the box is swiped right.
    // You can navigate to another screen or perform any other action.
  };

  return (
    <Background>
      <Logo />
      <View style={styles.headerContainer}>
        <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
      </View>

      {/* McDonald's Section with Sales Quote */}
      <TouchableWithoutFeedback onPress={handlePress}>
        <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
          <View>
            <Text style={styles.brandButtonText}>McDonald's</Text>
            <Text style={styles.salesQuote}>
              "I'm lovin' it!" - McDonald's
            </Text>
          </View>
          <Icon name="arrow-right" size={30} color="#007BFF" />
        </Animated.View>
      </TouchableWithoutFeedback>

      {/* Swipeable Box */}
      <PanGestureHandler onGestureEvent={handleSwipeRight}>
        <Animated.View style={styles.swipeableBox}>
          <Text style={styles.boxText}>Swipe me right!</Text>
        </Animated.View>
      </PanGestureHandler>

      {/* Repeating McDonald's sections as needed */}

      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>

    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.5)', // White with 50% transparency
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 10,
    borderRadius: 8,
    marginBottom: 20,
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF', // Presentable blue color
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  cartButton: {
    backgroundColor: 'blue',
    opacity: 0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
  swipeableBox: {
    backgroundColor: 'rgba(255, 0, 0, 0.5)', // Example background color (red with 50% transparency)
    height: 100,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 8,
    marginVertical: 10,
  },
  boxText: {
    fontSize: 18,
    color: '#fff',
  },
});
