import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated, ScrollView } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function Dashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));
  const [isEditing, setIsEditing] = useState(false);

  const handlePlusButtonPress = () => {
    setIsEditing(!isEditing);
  };

  const handlePress = (menu) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      if (menu === 'McDonalds') {
        navigation.navigate(menu + 'Menu');
      } else if (menu === 'Subway') {
        navigation.navigate(menu);
      } else if (menu === 'KFC') {
        navigation.navigate(menu);
      }
    });
  };

  const cartItems = [];

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <Background style={styles.pageBackground}>
        <View style={styles.headerContainer}>
          <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
        </View>

        <View style={styles.brandButtons}>
          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>McDonald's</Text>
              <Text style={styles.salesQuote}>
                "I'm lovin' it!" - McDonald's
              </Text>
              <Text style={styles.additionalInfo}>
                Serving Happiness Since 1955
              </Text>
              <Text style={styles.additionalInfo}>
                Find us at 1234 Fast Food Lane
              </Text>
            </View>
            <Icon name="arrow-right" size={30} color="#C70039" onPress={() => handlePress('McDonalds')}/>
          </Animated.View>

          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>Subway</Text>
              <Text style={styles.salesQuote}>
                "Eat Fresh!" - Subway
              </Text>
              <Text style={styles.additionalInfo}>
                Fresh Subs and Sandwiches
              </Text>
              <Text style={styles.additionalInfo}>
                Visit us at 8765 Sub Avenue
              </Text>
            </View>
            <Icon name="arrow-right" size={30} color="#C70039" onPress={() => handlePress('Subway')}/>
          </Animated.View>

          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>KFC</Text>
              <Text style={styles.salesQuote}>
                "Finger Lickin' Good!" - KFC
              </Text>
              <Text style={styles.additionalInfo}>
                Original Recipe Chicken since 1939
              </Text>
              <Text style={styles.additionalInfo}>
                Visit us at 4321 Fried Chicken Street
              </Text>
            </View>
            <Icon name="arrow-right" size={30} color="#C70039" onPress={() => handlePress('KFC')} />
          </Animated.View>
        </View>
      </Background>
      <View style={styles.bottomNavBar}>
        <TouchableOpacity style={styles.bottomNavIcon} onPress={() => navigation.navigate('CartIcon')}>
          <Icon name="shopping-cart" size={30} color="#C70039" />
          <Text style={styles.bottomNavText}>Cart</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.bottomNavIcon} onPress={() => navigation.navigate('ProfileIcon')}>
          <Icon name="user" size={30} color="#C70039" />
          <Text style={styles.bottomNavText}>Profile</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  pageBackground: {
    backgroundColor: '#F0A500',
  },
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 27,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandButtons: {
    flex: 1,
    justifyContent: 'center',
  },
  brandButton: {
    backgroundColor: '#E6D5B8',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 20,
    borderRadius: 10,
    marginBottom: 20,
    shadowColor: 'rgba(0, 0, 0, 0.2)',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.5,
    shadowRadius: 4,
    elevation: 3,
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#C70039',
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  additionalInfo: {
    fontSize: 16,
    textAlign: 'center',
    marginTop: 10,
    color: '#333',
  },
  bottomNavBar: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    padding: 20,
    backgroundColor: '#E6D5B8',
    borderTopWidth: 1,
    borderTopColor: '#D0BEA1',
  },
  bottomNavIcon: {
    alignItems: 'center',
  },
  bottomNavText: {
    fontSize: 16,
    color: '#C70039',
  },
});
