import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ onPress, cartItems, totalPrice }) {
  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      <View style={styles.cartInfo}>
        <Text style={styles.cartItemCount}>{cartItems.length}</Text>
        <Text style={styles.cartTotalPrice}>${totalPrice.toFixed(2)}</Text>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartInfo: {
    position: 'absolute',
    top: -10,
    right: -10,
    backgroundColor: 'red',
    borderRadius: 10,
    width: 20,
    height: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  cartItemCount: {
    color: 'white',
    fontSize: 12,
  },
  cartTotalPrice: {
    color: 'white',
    fontSize: 12,
  },
});
