import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';
import Carousel from 'react-native-snap-carousel';

export default function Dashboard({ navigation }) {
  const fastFoodBrand = 'FastFood Express';
  const foodItems = [
    { name: 'Pizza', price: '$10.99' },
    { name: 'Burger', price: '$8.99' },
    { name: 'Sushi', price: '$12.99' },
    { name: 'Fries', price: '$4.99' },
    { name: 'Hot Dog', price: '$6.99' },
    // Add more food items as needed
  ];

  const renderItem = ({ item }) => (
    <TouchableOpacity
      style={styles.foodItem}
      onPress={() => {
        navigation.navigate('FoodItemDetails', { item });
      }}
    >
      <Text style={styles.foodItemName}>{item.name}</Text>
      <Text style={styles.foodItemPrice}>{item.price}</Text>
    </TouchableOpacity>
  );

  return (
    <Background>
      <View style={styles.headerContainer}>
        <Logo />
        <Header>Welcome to {fastFoodBrand}</Header>
      </View>

      <Text style={styles.sectionTitle}>Menu</Text>

      {/* Wrap Carousel with a ScrollView */}
      <ScrollView horizontal>
        <Carousel
          data={foodItems}
          renderItem={renderItem}
          sliderWidth={300}
          itemWidth={200}
          layout={'default'}
          loop={true}
        />
      </ScrollView>

      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>
    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginHorizontal: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  cartButton: {
    backgroundColor: 'blue',
    opacity: 0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
