import React, { createContext, useContext, useState, useEffect } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Load the user-specific cart from local storage on component mount
    const userCart = localStorage.getItem(getUserCartKey());
    if (userCart) {
      setCartItems(JSON.parse(userCart));
    }
  }, []);

  useEffect(() => {
    // Save the user-specific cart to local storage whenever it changes
    localStorage.setItem(getUserCartKey(), JSON.stringify(cartItems));
  }, [cartItems]);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name);
    setCartItems(updatedCart);
  };

  const clearCart = () => {
    setCartItems([]);
  };

  // Get a unique user-specific cart key based on user authentication
  function getUserCartKey() {
    // Implement your authentication logic to obtain the user identifier (e.g., user ID or username)
    // Replace the following with your actual authentication logic
    const userId = getAuthenticatedUserId();
    return `userCart_${userId}`;
  }

  // Replace this with your authentication logic to get the user's ID
  function getAuthenticatedUserId() {
    // Example: You might get the user ID from a token or your authentication context
    return 'user123'; // Replace with the actual user ID retrieval logic
  }

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
