import React, { useState } from 'react';
import { View, Text, StyleSheet, Animated, TouchableWithoutFeedback } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import ProfileIcon from './ProfileIcon'; // Import the ProfileIcon component

export default function Dashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));
  const [showProfileIcon, setShowProfileIcon] = useState(false);

  const toggleProfileIcon = () => {
    setShowProfileIcon(!showProfileIcon);
  };

  const handlePress = (menu) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      if (menu === 'McDonalds') {
        navigation.navigate(menu + 'Menu');
      } else if (menu === 'Subway') {
        navigation.navigate(menu);
      } else if (menu === 'KFC') {
        navigation.navigate(menu);
      }
      // Add more conditions for other fast food sections if needed
    });
  };

  return (
    <Background>
      <Logo />
      <View style={styles.headerContainer}>
        <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
      </View>

      {/* Profile Icon Button */}
      <ProfileIcon onPress={toggleProfileIcon} />

      {/* McDonald's Section with Sales Quote */}
      <TouchableWithoutFeedback onPress={() => handlePress('McDonalds')}>
        <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
          <View>
            <Text style={styles.brandButtonText}>McDonald's</Text>
            <Text style={styles.salesQuote}>
              "I'm lovin' it!" - McDonald's
            </Text>
            {/* Additional Information */}
            <Text style={styles.additionalInfo}>
              Serving Happiness Since 1955
            </Text>
            <Text style={styles.additionalInfo}>
              Find us at 1234 Fast Food Lane
            </Text>
          </View>
          {/* ... */}
        </Animated.View>
      </TouchableWithoutFeedback>

      {/* Subway Section */}
      <TouchableWithoutFeedback onPress={() => handlePress('Subway')}>
        <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
          <View>
            <Text style={styles.brandButtonText}>Subway</Text>
            {/* ... */}
          </View>
          {/* ... */}
        </Animated.View>
      </TouchableWithoutFeedback>

      {/* KFC Section */}
      <TouchableWithoutFeedback onPress={() => handlePress('KFC')}>
        <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
          <View>
            <Text style={styles.brandButtonText}>KFC</Text>
            {/* ... */}
          </View>
          {/* ... */}
        </Animated.View>
      </TouchableWithoutFeedback>

      {/* Repeating fast food sections as needed */}
      {/* ... (you can add more fast-food sections here) */}

      {/* Cart Button */}
      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>
    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 10,
    borderRadius: 8,
    marginBottom: 20,
    height: 180,
    width: 300,
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF',
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  additionalInfo: {
    fontSize: 16,
    textAlign: 'center',
    marginTop: 10,
    color: '#333',
  },
  cartButton: {
    backgroundColor: 'blue',
    opacity: 0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
