import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Modal, TextInput, Button, ScrollView } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useNavigation } from '@react-navigation/native';

export default function Seller() {
  const navigation = useNavigation();
  const [isModalVisible, setModalVisible] = useState(false);
  const [fastFoodName, setFastFoodName] = useState('');
  const [fastFoodMenu, setFastFoodMenu] = useState('');
  const [fastFoodPrice, setFastFoodPrice] = useState('');
  const [postedFastFoods, setPostedFastFoods] = useState([]);
  
  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  const handleSearch = () => {
    // Create a new array with the existing fast food items and add the new one
    const newItem = {
      name: fastFoodName,
      menu: fastFoodMenu,
      price: fastFoodPrice,
    };
    const updatedFastFoods = [...postedFastFoods, newItem];
    setPostedFastFoods(updatedFastFoods);

    // Clear the input fields
    setFastFoodName('');
    setFastFoodMenu('');
    setFastFoodPrice('');

    // Close the modal
    toggleModal();
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>

      <Text style={styles.sectionTitle}>Seller</Text>

      <TouchableOpacity onPress={toggleModal} style={styles.button}>
        <Text style={styles.buttonText}>Post a Fast Food</Text>
      </TouchableOpacity>

      <ScrollView style={styles.fastFoodList}>
        {postedFastFoods.map((item, index) => (
          <View key={index} style={styles.fastFoodItem}>
            <Text style={styles.fastFoodName}>{item.name}</Text>
            <Text style={styles.fastFoodMenu}>{item.menu}</Text>
            <Text style={styles.fastFoodPrice}>Price: {item.price}</Text>
          </View>
        ))}
      </ScrollView>

      <Modal
        animationType="slide"
        transparent={true}
        visible={isModalVisible}
      >
        <View style={styles.modalContainer}>
          <Text style={styles.modalTitle}>Post Fast Food</Text>
          <TextInput
            placeholder="Fast Food Name"
            onChangeText={(text) => setFastFoodName(text)}
            value={fastFoodName}
            style={styles.input}
          />
          <TextInput
            placeholder="Fast Food Menu"
            onChangeText={(text) => setFastFoodMenu(text)}
            value={fastFoodMenu}
            style={styles.input}
          />
          <TextInput
            placeholder="Fast Food Price"
            onChangeText={(text) => setFastFoodPrice(text)}
            value={fastFoodPrice}
            style={styles.input}
          />
          <Button title="Post" onPress={handleSearch} />
          <Button title="Close" onPress={toggleModal} />
        </View>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 19,
    paddingHorizontal: 20,
    justifyContent: 'center',
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  backButton: {
    position: 'absolute',
    left: 20,
    top: 20,
  },
  button: {
    marginTop: 20,
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: 'white',
    textAlign: 'center',
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 20,
  },
  modalTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  fastFoodList: {
    marginTop: 10,
  },
  fastFoodItem: {
    backgroundColor: 'lightgray',
    borderRadius: 8,
    padding: 10,
    marginBottom: 10,
  },
  fastFoodName: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  fastFoodMenu: {
    fontSize: 16,
  },
  fastFoodPrice: {
    fontSize: 16,
    color: 'green',
  },
});
