import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ cartItems, removeFromCart, calculateTotalPrice }) {
  return (
    <TouchableOpacity style={styles.cartIconContainer}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      {cartItems.length > 0 && ( // Render cart content if items are in the cart
        <View style={styles.cartInfo}>
          <Text style={styles.cartItemCount}>{cartItems.length}</Text>
          <Text style={styles.cartTotalPrice}>${calculateTotalPrice().toFixed(2)}</Text>
        </View>
      )}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    alignItems: 'center',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartInfo: {
    marginLeft: 5,
    backgroundColor: '#007BFF',
    padding: 5,
    borderRadius: 10,
  },
  cartItemCount: {
    color: 'white',
    fontSize: 14,
  },
  cartTotalPrice: {
    color: 'white',
    fontSize: 14,
    fontWeight: 'bold',
  },
});
