import React, { useState } from 'react';
import { TouchableOpacity, StyleSheet, Text, View, ScrollView, Modal, Button, Alert, ActivityIndicator } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { useCart } from '../../CartContext';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function CartIcon() {
  const navigation = useNavigation();
  const { cartItems, removeFromCart, resetCart } = useCart();
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
  };

  const handleBuy = () => {
    if (cartItems.length > 0) {
      setIsModalVisible(true);
    } else {
      // Cart is empty, show an alert
      Alert.alert('Empty Cart', 'Your cart is empty. Please add items to your cart before purchasing.');
    }
  };

  const closeModal = () => {
    setIsModalVisible(false);
  };

  const handleBuyFromModal = () => {
    setIsLoading(true); // Set loading to true to show a loading indicator

    // Simulate a purchase process with a delay (You can replace this with your actual purchase logic)
    setTimeout(() => {
      setIsLoading(false); // Set loading back to false when the process is complete
      resetCart(); // Reset the cart
      setIsModalVisible(false); // Close the modal
    }, 2000); // Simulate a 2-second purchase process
  };

  return (
    <>
      <TouchableOpacity
        onPress={() => navigation.navigate('Dashboard')}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>

      <Modal visible={isModalVisible} animationType="slide">
        <View style={styles.modal}>
          <Text style={styles.modalTitle}>Purchase Summary</Text>
          <ScrollView style={styles.cartItemsContainer}>
            {cartItems.map((item, index) => (
              <View key={index} style={styles.cartItem}>
                <Text style={styles.cartItemName}>{item.name}</Text>
                <Text style={styles.cartItemPrice}>{item.price}</Text>
              </View>
            ))}
            <Text style={styles.cartTotal}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
            {isLoading ? (
              <ActivityIndicator size="large" color="#007BFF" />
            ) : (
              <>
                <Button title="Buy" onPress={handleBuyFromModal} />
                <Button title="Close" onPress={closeModal} />
              </>
            )}
          </ScrollView>
        </View>
      </Modal>

      {cartItems.length > 0 ? (
        <ScrollView style={styles.cartItemsContainer}>
          <View style={styles.cartTitle}>
            <Text style={styles.cartTitleText}>CART</Text>
            <View style={styles.cartTitleLine}></View>
          </View>
          {cartItems.map((item, index) => (
            <View key={index} style={styles.cartItem}>
              <Text style={styles.cartItemName}>{item.name}</Text>
              <Text style={styles.cartItemPrice}>{item.price}</Text>
              <TouchableOpacity onPress={() => removeFromCart(item)} style={styles.removeButton}>
                <Text style={styles.removeButtonText}>Remove</Text>
              </TouchableOpacity>
            </View>
          ))}
          <Text style={styles.cartTotal}>-----------------------Total: ${calculateTotalPrice().toFixed(2)}</Text>
          <TouchableOpacity onPress={handleBuy} style={styles.buyButton}>
            <Text style={styles.buyButtonText}>Buy</Text>
          </TouchableOpacity>
        </ScrollView>
      ) : (
        <Text style={styles.emptyCartText}>Your cart is empty.</Text>
      )}
    </>
  );
}

const styles = StyleSheet.create({
  backButton: {
    position: 'absolute',
    backgroundColor: 'transparent',
    marginTop: 35,
    marginLeft: 10,
  },
  modal: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
  },
  modalTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  cartTotal: {
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 10,
    textAlign: 'center', 
  },
  cartItemsContainer: {
    marginTop: 160,
  },
  cartTitle: {
    alignItems: 'center',
  },
  cartTitleText: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  cartTitleLine: {
    borderBottomWidth: 2,
    borderBottomColor: 'black',
    width: 1110,
    marginTop: 5,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    paddingHorizontal: 20,
    paddingVertical: 10,
    backgroundColor: '#F5F5F5',
    borderRadius: 5,
  },
  cartItemName: {
    fontSize: 20,
    flex: 1,
  },
  cartItemPrice: {
    fontSize: 20,
    fontWeight: 'bold',
    marginRight: 20,
  },
  removeButton: {
    backgroundColor: '#FF5733',
    padding: 5,
    borderRadius: 5,
  },
  removeButtonText: {
    color: 'white',
    fontSize: 12,
  },
  emptyCartText: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 180,
    textAlign: 'center',
  },
  buyButton: {
    backgroundColor: '#007BFF',
    padding: 10,
    borderRadius: 5,
    marginTop: 20,
  },
  buyButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});
