import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ cartItems, onPress }) {
  const totalItems = cartItems.length;
  const totalAmount = cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);

  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      {totalItems > 0 && (
        <View style={styles.cartInfo}>
          <Text style={styles.cartItemCount}>{totalItems}</Text>
          <Text style={styles.cartTotalPrice}>${totalAmount.toFixed(2)}</Text>
        </View>
      )}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    alignItems: 'center',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartInfo: {
    marginLeft: 5,
    backgroundColor: '#007BFF',
    padding: 5,
    borderRadius: 10,
  },
  cartItemCount: {
    color: 'white',
    fontSize: 14,
  },
  cartTotalPrice: {
    color: 'white',
    fontSize: 14,
    fontWeight: 'bold',
  },
});
