import React, { useState } from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ onPress }) {
  const [cartItems, setCartItems] = useState([]); 

  const handleAddToCart = (item) => {
    setCartItems([...cartItems, item]); 
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name); 
    setCartItems(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
  };

  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      <View style={styles.cartInfo}>
        <Text style={styles.cartItemCount}>{cartItems.length} items</Text>
        <Text style={styles.cartTotal}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
      </View>
      {/* "Add to Cart" button */}
      <TouchableOpacity style={styles.addToCartButton} onPress={() => handleAddToCart({ name: 'Custom Item', price: '$3.99' })}>
        <Text style={styles.addToCartButtonText}>Add to Cart</Text>
      </TouchableOpacity>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
});
