import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useNavigation } from '@react-navigation/native';
import Modal from 'react-native-modal';

export default function Seller() {
  const navigation = useNavigation();
  const [isModalVisible, setModalVisible] = useState(false);
  const [fastFoodName, setFastFoodName] = useState('');
  const [fastFoodMenu, setFastFoodMenu] = useState('');
  const [fastFoodPrice, setFastFoodPrice] = useState('');

  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  const saveFastFoodItem = () => {
    // You can save the fast food item to your data structure or perform any other actions here.
    // Example: save to state or context
    console.log('Name:', fastFoodName);
    console.log('Menu:', fastFoodMenu);
    console.log('Price:', fastFoodPrice);

    // Close the modal
    toggleModal();
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>

      <Text style={styles.sectionTitle}>Seller</Text>

      <TouchableOpacity onPress={toggleModal} style={styles.addButton}>
        <Text style={styles.addButtonLabel}>Add Fast Food Item</Text>
      </TouchableOpacity>

      <Modal isVisible={isModalVisible}>
        <View style={styles.modalContent}>
          <Text style={styles.modalTitle}>Add Fast Food Item</Text>
          <TextInput
            placeholder="Fast Food Name"
            value={fastFoodName}
            onChangeText={(text) => setFastFoodName(text)}
            style={styles.input}
          />
          <TextInput
            placeholder="Fast Food Menu"
            value={fastFoodMenu}
            onChangeText={(text) => setFastFoodMenu(text)}
            style={styles.input}
          />
          <TextInput
            placeholder="Fast Food Price"
            value={fastFoodPrice}
            onChangeText={(text) => setFastFoodPrice(text)}
            style={styles.input}
          />
          <TouchableOpacity onPress={saveFastFoodItem} style={styles.saveButton}>
            <Text style={styles.saveButtonText}>Save</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={toggleModal} style={styles.cancelButton}>
            <Text style={styles.cancelButtonText}>Cancel</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 19,
    paddingHorizontal: 20,
    justifyContent: 'center',
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  backButton: {
    position: 'absolute',
    left: 20,
    top: 20,
  },
  addButton: {
    marginTop: 20,
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
  },
  addButtonLabel: {
    color: 'white',
    fontWeight: 'bold',
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    padding: 10,
    borderRadius: 5,
    marginBottom: 10,
  },
  saveButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
    marginTop: 10,
  },
  saveButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
  cancelButton: {
    backgroundColor: 'red',
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
    marginTop: 10,
  },
  cancelButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
});
