import * as React from "react";
import { LinearGradient } from "expo-linear-gradient";
import { Text, TextInput, View, StyleSheet, TouchableOpacity, Alert } from "react-native";
import Axios from "axios";
import { useNavigation } from "@react-navigation/native";
import Icon from "react-native-vector-icons/FontAwesome";

export default function RegisterScreen() {
  const navigation = useNavigation();

  const register = async () => {
    console.log(
      "username:" +
        data1.username +
        " " +
        "password" +
        data1.password +
        " " +
        data1.confirmPassword
    );
    await Axios.post("http://localhost:8000/data/register", {
      username: data1.username,
      password: data1.password,
      confirmPassword: data1.confirmPassword,
    })
      .then((response) => {
        console.log("response", response);
        // Show an alert text if the registration was successful
        if (response.status === 200) {
          Alert.alert("Registration successful!", "You have successfully registered your account.");
          // Navigate to the login screen
          navigation.navigate("Login");
        }
      })
      .catch((e) => {
        alert("error happened");
      });
  };

  const [data1, setData] = React.useState({
    username: "",
    password: "",
    confirmPassword: "",
    isValidUser: true,
    isValidPassword: true,
  });

  // ...

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="white" />
      </TouchableOpacity>
      <View style={styles.View1}>
        <Text style={styles.userdata}>Username:</Text>
        <TextInput
          placeholder="Enter Username"
          style={styles.username}
          onChangeText={(e) => setUsername(e)}
        />
        <Text style={styles.userdata}>Password</Text>
        <TextInput
          placeholder="Password"
          style={styles.username}
          onChangeText={(e) => setPassword(e)}
          secureTextEntry
        />
        <Text style={styles.userdata}>Confirm Password</Text>
        <TextInput
          placeholder="Confirm Password"
          style={styles.username}
          onChangeText={(e) => setConfirmPassword(e)}
          secureTextEntry
        />
        <TouchableOpacity>
          <LinearGradient
            colors={["#f72855", "#f72855"]}
            style={styles.LoginUser}
          >
            <Text style={styles.Button1} onPress={register}>
              SignUp
            </Text>
          </LinearGradient>
        </TouchableOpacity>
      </View>
    </View>
  );
}
  const styles = StyleSheet.create({
    container: {
      height: "100%",
      width: "100%",
      flex: '1',
      width: '100%',
      backgroundColor: 'rgb(255, 190, 123)',
    },
    View1: {
      marginTop: 60,
      backgroundColor: "rgba(255, 190, 123, 0.5)", 
      height: 700,
      paddingTop: 80,
      alignContent: 'center',
      alignSelf: "center",
      width: "95%",
      borderRadius: 20,
    },
    backButton: {
      position: "absolute",
      top: 20,
      left: 20,
      zIndex: 1,
    },
    username: {
      fontFamily: "sans-serif", 
      alignSelf: "center",
      padding: 18,
      textAlign: "center",
      borderBottomColor: "rgba(100,50,40,0.2)",
      borderBottomWidth: 1,
      borderWidth: 2,
      borderColor: "rgba(100,50,40,0.2)",
      width: "70%",
      backgroundColor: "#d8dbd7",
      justifyContent: "center",
      fontSize: 20,
      borderRadius: 10,
    },
    userdata: {
      paddingTop: 20,
      paddingBottom: 10,
      alignSelf: "center",
      color: "#eff2e9",
      fontSize: 20,
      justifyContent: "center",
    },
    LoginUser: {
      justifyContent: "center",
      alignContent: "center",
      alignSelf: "center",
      marginTop: 30,
      padding: 15,
      borderRadius: 10,
      width: "70%",
    },
    Button1: {
      alignSelf: "center",
      fontSize: 20,
      color: "white",
    },
  });
