import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, SafeAreaView } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import * as Animatable from "react-native-animatable";
import { LinearGradient } from 'expo-linear-gradient';
import Axios from "axios";
import Logo from '../components/Logo';

export default function LoginScreen({ navigation }) {
  const [state, setState] = useState({
    username: "",
    password: "",
    isValidUser: true,
    isValidPassword: true,
    checkTextInputChange: true,
  });

  const textUsernameChange = (val) => {
    setState(prevState => ({
      ...prevState,
      username: val,
      checkTextInputChange: val.length >= 4
    }));
  };

  const handlePasswordChange = (val) => {
    setState(prevState => ({
      ...prevState,
      password: val,
      isValidPassword: val.length >= 6,
    }));
  };

  const handleUser = (val) => {
    setState(prevState => ({
      ...prevState,
      isValidUser: val.trim().length >= 4,
    }));
  };

  const LoginData = () => {
    console.log("username", state.username);
    console.log("password", state.password);

    if (state.username.trim() === "" || state.password.trim() === "") {
      alert("Please enter both email and password.");
      return;
    }

    Axios.post("http://localhost:8000/data/signin", {
      username: state.username,
      password: state.password,
    })
    .then((response) => {
      if (response.data.status === "success") {
        navigation.navigate("Profile", { username: state.username });
      } else {
        alert("Email and password do not match.");
      }
    })
    .catch((e) => {
      alert("An error occurred during login.");
    });
  };

  return (
    <SafeAreaView style={styles.container}>
      <StatusBar style="auto" />
      <View style={styles.data}>
        <Animatable.View animation="rubberBand" duration={1000}>
          <View style={styles.logoContainer}>
            <Logo style={styles.logo} />
          </View>
          <Text style={styles.applego}>APPLEGO</Text>

          <Text style={styles.userdata}>Username</Text>
          <TextInput
            placeholder="Enter Username"
            style={styles.username}
            onChangeText={(e) => textUsernameChange(e)}
            onEndEditing={(e) => handleUser(e.nativeEvent.text)}
          />
          {!state.isValidUser && (
            <Animatable.View>
              <Text style={styles.error}>
                Username should be 4 characters long
              </Text>
            </Animatable.View>
          )}

          <Text style={styles.userdata}>Password</Text>
          <TextInput
            secureTextEntry
            placeholder="Password"
            style={styles.username}
            onChangeText={(e) => handlePasswordChange(e)}
          />
          {!state.isValidPassword && (
            <Animatable.View animation="fadeInLeft" duration={500}>
              <Text style={styles.error}>
                Password should be 6 characters long
              </Text>
            </Animatable.View>
          )}

          <TouchableOpacity onPress={() => navigation.navigate('ResetPasswordScreen')}>
            <Text style={styles.forgotPassword}>
              Forgot Password ?
            </Text>
          </TouchableOpacity>

          <TouchableOpacity>
            <LinearGradient colors={['#f72855', '#f72855']} style={styles.LoginUser}>
              <Text style={[styles.Button1, { fontWeight: "bold", color: "white" }]} onPress={LoginData}>
                Login
              </Text>
            </LinearGradient>
            <Text style={styles.signup} onPress={() => navigation.navigate('RegisterScreen')}>
              Don't have an account? Sign Up
            </Text>
          </TouchableOpacity>
        </Animatable.View>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "lightgrey",
  },
  data: {
    marginTop: 60,
    backgroundColor: "rgba(255, 190, 123, 0.6)",
    height: 700,
    paddingTop: 80,
    alignItems: 'center',
    width: "95%",
    borderRadius: 20,
    alignSelf: 'center',
  },
  username: {
    fontFamily: "sans-serif",
    alignSelf: "center",
    padding: 15,
    textAlign: "center",
    borderBottomColor: "rgba(100,50,40,0.2)",
    borderBottomWidth: 1,
    width: "80%",
    backgroundColor: "#d8dbd7",
    fontSize: 20,
    borderRadius: 10,
  },
  error: {
    color: "red",
    fontSize: 15,
    paddingLeft: 80,
  },
  LoginUser: {
    justifyContent: "center",
    alignItems: 'center',
    marginTop: 20,
    padding: 15,
    width: "80%",
    borderRadius: 10,
  },
  Button1: {
    alignSelf: "center",
    fontSize: 20,
    justifyContent: "space-between",
  },
  userdata: {
    paddingTop: 20,
    paddingBottom: 10,
    alignSelf: "center",
    color: "#eff2e9",
    fontSize: 20,
    justifyContent: "center",
  },
  applego: {
    paddingBottom: 20,
    alignSelf: "center",
    color: "#eff2e9",
    fontSize: 40,
    fontWeight: "bold",
    justifyContent: "center",
  },
  signup: {
    paddingTop: 10,
    color: "#eff2e9",
    fontSize: 17,
    alignSelf: "center",
  },
});
