import React from 'react';
import { TouchableOpacity, StyleSheet, Text, View } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { useCart } from '../../CartContext'; 
import Icon from 'react-native-vector-icons/FontAwesome';

export default function CartIcon() {
  const navigation = useNavigation();
  const { cartItems, removeFromCart } = useCart(); // Add a removeFromCart function

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
  };

  return (
    <>
      <TouchableOpacity
        onPress={() => navigation.navigate('Dashboard')} 
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>
     
      {cartItems.length > 0 && (
        <View style={styles.cartItems}>
          {cartItems.map((item, index) => (
            <View key={index} style={styles.cartItem}>
              <Text style={styles.cartItemName}>{item.name}</Text>
              <Text style={styles.cartItemPrice}>${item.price}</Text>
              <TouchableOpacity onPress={() => removeFromCart(item)} style={styles.removeButton}>
                <Text style={styles.removeButtonText}>Remove</Text>
              </TouchableOpacity>
            </View>
          ))}
          <Text style={styles.cartTotal}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
        </View>
      )}
    </>
  );
}

const styles = StyleSheet.create({
  backButton: {
    position: 'absolute',
    backgroundColor: 'transparent',
    marginTop: 15,
    marginLeft: 10,
  },
  cartTotal: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 5,
    textAlign: 'center', // Center-align the total
  },
  cartItems: {
    marginTop: 100,
    alignItems: 'center', // Center-align the entire cart items section
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 5,
    paddingHorizontal: 10,
  },
  cartItemName: {
    fontSize: 14,
    flex: 1,
    textAlign: 'center',
    marginRight: 10,
  },
  cartItemPrice: {
    fontSize: 14,
    fontWeight: 'bold',
  },
  removeButton: {
    backgroundColor: '#FF5733',
    padding: 5,
    borderRadius: 5,
  },
  removeButtonText: {
    color: 'white',
  },
});
