import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ onPress, cartItems, total, handleAddToCart }) {
  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      <View style={styles.cartInfo}>
        <Text style={styles.cartItemCount}>{cartItems.length} items</Text>
        <Text style={styles.cartTotal}>Total: ${total.toFixed(2)}</Text>
      </View>
      {/* "Add to Cart" button */}
      <TouchableOpacity style={styles.addToCartButton} onPress={() => handleAddToCart({ name: 'Custom Item', price: '$3.99' })}>
        <Text style={styles.addToCartButtonText}>Add to Cart</Text>
      </TouchableOpacity>
      {/* Cart */}
      <View style={styles.cartContainer}>
        <Text style={styles.cartTitle}>Cart</Text>
        {cartItems.map((cartItem, cartIndex) => (
          <View key={cartIndex} style={styles.cartItem}>
            <Text style={styles.cartItemName}>{cartItem.name}</Text>
            <Text style={styles.cartItemPrice}>{cartItem.price}</Text>
            <TouchableOpacity
              style={styles.removeFromCartButton}
              onPress={() => removeFromCart(cartItem)}
            >
              <Text style={styles.removeFromCartButtonText}>Remove</Text>
            </TouchableOpacity>
          </View>
        ))}
        <Text style={styles.totalPrice}>Total: ${total.toFixed(2)}</Text>
      </View>
    </TouchableOpacity>
  );
}
const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
});
