import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useNavigation } from '@react-navigation/native';
import CartIcon from './CartIcon'; // Import your CartIcon component here

export default function Subway() {
  const navigation = useNavigation();

  const menuItems = [
    { name: 'Italian BMT', price: '$6.99' },
    { name: 'Chicken Teriyaki', price: '$7.49' },
    { name: 'Veggie Delite', price: '$5.99' },
  ];

  const [pressedMenuItem, setPressedMenuItem] = useState(null);
  const [cartItems, setCartItems] = useState([]); 
  const [cartVisible, setCartVisible] = useState(false); 

  const toggleCartIcon = () => {
    setCartVisible(!cartVisible);
  };

  const addToCart = (item) => {
    setCartItems([...cartItems, item]); 
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name); 
    setCartItems(updatedCart);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>
      <CartIcon onPress={toggleCartIcon} cartCount={cartItems.length} /> {/* Add CartIcon component */}
      <Text style={styles.sectionTitle}>KFC Menu</Text>
      <ScrollView style={styles.foodItemsContainer}>
        {menuItems.map((item, itemIndex) => (
          <TouchableOpacity
            key={itemIndex}
            style={[
              styles.foodItem,
              { backgroundColor: pressedMenuItem === item.name ? '#FFD700' : '#fff' },
            ]}
            onPress={() => {
              navigation.navigate('FoodItemDetails', { item });
            }}
            onShowUnderlay={() => setPressedMenuItem(item.name)}
            onHideUnderlay={() => setPressedMenuItem(null)}
          >
            <>
              <Text style={styles.foodItemName}>{item.name}</Text>
              <Text style={styles.foodItemPrice}>{item.price}</Text>
              <TouchableOpacity
                style={styles.addToCartButton}
                onPress={() => addToCart(item)}
              >
                <Text style={styles.addToCartButtonText}>Add to Cart</Text>
              </TouchableOpacity>
            </>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {/* Cart */}
      {cartVisible && (
        <View style={styles.cartContainer}>
          <Text style={styles.cartTitle}>Cart</Text>
          {cartItems.map((cartItem, cartIndex) => (
            <View key={cartIndex} style={styles.cartItem}>
              <Text style={styles.cartItemName}>{cartItem.name}</Text>
              <Text style={styles.cartItemPrice}>{cartItem.price}</Text>
              <TouchableOpacity
                style={styles.removeFromCartButton}
                onPress={() => removeFromCart(cartItem)}
              >
                <Text style={styles.removeFromCartButtonText}>Remove</Text>
              </TouchableOpacity>
            </View>
          ))}
        </View>
      )}
    </View>
  );
}
