import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated } from 'react-native'; // Import Animated from react-native
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';
import McDonaldsMenu from '../components/fastfood/McDonaldsMenu';

export default function Dashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1)); // Initialize scale animation value

  const handlePress = () => {
    // Animate the scale to make the section appear pressed
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      // Navigate to McDonald's menu screen after animation
      navigation.navigate('McDonaldsMenu');
    });
  };

  return (
    <Background>
      <Logo />
      <View style={styles.headerContainer}>
        <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
      </View>

      {/* McDonald's Section */}
      <TouchableWithoutFeedback onPress={handlePress}>
        <Animated.View style={[styles.brandContainer, { transform: [{ scale: scaleValue }] }]}>
          <Text style={styles.brandName}>McDonald's</Text>
          <Icon name="arrow-right" size={30} color="#000" />
        </Animated.View>
      </TouchableWithoutFeedback>

      {/* Repeating McDonald's sections as needed */}

      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>

    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  brandName: {
    fontSize: 24,
    fontWeight: 'bold',
    marginRight: 10,
  },
  brandIcon: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  cartButton: {
    backgroundColor: 'blue',
    opacity: 0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
