import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ onPress, cartItems, total, addToCart }) {
  const handleAddToCart = () => {
    // Invoke the addToCart function passed as a prop
    addToCart({ name: 'New Item', price: '$X.XX' });
  };

  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      <View style={styles.cartInfo}>
        <Text style={styles.cartItemCount}>{cartItems.length} items</Text>
        <Text style={styles.cartTotal}>Total: ${total.toFixed(2)}</Text>
      </View>
      {/* Add a button to trigger addToCart */}
      <TouchableOpacity style={styles.addToCartButton} onPress={handleAddToCart}>
        <Text style={styles.addToCartButtonText}>Add New Item</Text>
      </TouchableOpacity>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    alignItems: 'center',
  },
  cartIcon: {
    fontSize: 24,
    marginRight: 5,
  },
  cartInfo: {
    marginLeft: 5,
    justifyContent: 'center',
    alignItems: 'flex-end',
  },
  cartItemCount: {
    fontSize: 12,
    fontWeight: 'bold',
  },
  cartTotal: {
    fontSize: 14,
    fontWeight: 'bold',
  },
  addToCartButton: {
    backgroundColor: '#008000', // Green color for the "Add New Item" button
    paddingVertical: 5,
    paddingHorizontal: 10,
    borderRadius: 5,
  },
  addToCartButtonText: {
    color: 'white',
    fontSize: 14,
    fontWeight: 'bold',
  },
});
