import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Button from '../components/Button';
import Carousel from 'react-native-snap-carousel'; // Import the carousel component

export default function Dashboard({ navigation }) {
  // Dummy data for food items
  const foodItems = [
    { name: 'Pizza', price: '$10.99' },
    { name: 'Burger', price: '$8.99' },
    { name: 'Sushi', price: '$12.99' },
    // Add more food items as needed
  ];

  // Render each food item as a slide
  const renderFoodItem = ({ item, index }) => (
    <TouchableOpacity
      key={index}
      style={styles.foodItem}
      onPress={() => {
        // Handle item selection, e.g., navigate to item details screen
        navigation.navigate('FoodItemDetails', { item });
      }}
    >
      <Text style={styles.foodItemName}>{item.name}</Text>
      <Text style={styles.foodItemPrice}>{item.price}</Text>
    </TouchableOpacity>
  );

  return (
    <Background>
      <View style={styles.headerContainer}>
        <Logo />
        <Header>Welcome to Food Delivery</Header>
      </View>

      <Carousel
        data={foodItems}
        renderItem={renderFoodItem}
        sliderWidth={300}
        itemWidth={300}
        layout={'default'}
        loop={true} // Enable looping of the slides
      />

      <Button
        mode="outlined"
        onPress={() =>
          navigation.reset({
            index: 0,
            routes: [{ name: 'StartScreen' }],
          })
        }
        style={styles.logoutButton}
      >
        Logout
      </Button>
    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  foodItemsContainer: {
    flex: 1,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  logoutButton: {
    marginTop: 20,
  },
});
