import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ onPress, cartItems, calculateTotalPrice }) {
  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      {cartItems.length > 0 && (
        <View style={styles.cartTotalContainer}>
          <Text style={styles.totalPrice}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
          <View style={styles.cartItemList}>
            {cartItems.map((item, index) => (
              <View key={index} style={styles.cartItem}>
                <Text style={styles.cartItemName}>{item.name}</Text>
                <Text style={styles.cartItemPrice}>{item.price}</Text>
              </View>
            ))}
          </View>
        </View>
      )}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartTotalContainer: {
    backgroundColor: 'white',
    position: 'absolute',
    top: 70,
    right: 20,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
    padding: 10,
    zIndex: 1,
  },
  cartItemList: {
    marginTop: 10,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 5,
  },
  cartItemName: {
    fontSize: 14,
  },
  cartItemPrice: {
    fontSize: 14,
    fontWeight: 'bold',
  },
  totalPrice: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
  },
});
