import React, { createContext, useContext, useState } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) { 
  const [cartItems, setCartItems] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);

  const addToCart = (item) => {
    // Ensure a user is logged in
    if (currentUser) {
      // Add the item to the cart, associating it with the current user
      setCartItems([...cartItems, { user: currentUser, item }]);
    }
  };

  const removeFromCart = (item) => {
    // Ensure a user is logged in
    if (currentUser) {
      // Remove the item from the cart, associated with the current user
      const updatedCart = cartItems.filter((cartItem) => cartItem.user === currentUser && cartItem.item.name !== item.name);
      setCartItems(updatedCart);
    }
  };

  const clearCart = () => {
    // Ensure a user is logged in
    if (currentUser) {
      // Clear the cart of the current user
      const updatedCart = cartItems.filter((cartItem) => cartItem.user !== currentUser);
      setCartItems(updatedCart);
    }
  };

  // Method to set the current user
  const login = (user) => {
    setCurrentUser(user);
  };

  // Method to log out
  const logout = () => {
    setCurrentUser(null);
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart, login, logout }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
