import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import CartIcon from './CartIcon';

export default function Subway() {
  const menuItems = [
    { id: 1, name: 'Italian BMT', price: '$6.99' },
    { id: 2, name: 'Chicken Teriyaki', price: '$7.49' },
    { id: 3, name: 'Veggie Delite', price: '$5.99' },
  ];

  const [cartItems, setCartItems] = useState([]);
  
  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const calculateCartCount = () => {
    return cartItems.length;
  };

  return (
    <View style={styles.container}>
      <CartIcon cartCount={calculateCartCount()} />
      <Text style={styles.sectionTitle}>Subway Menu</Text>
      <ScrollView style={styles.foodItemsContainer}>
        {menuItems.map((item) => (
          <View key={item.id} style={styles.foodItem}>
            <Text style={styles.foodItemName}>{item.name}</Text>
            <Text style={styles.foodItemPrice}>{item.price}</Text>
            <TouchableOpacity
              style={styles.addToCartButton}
              onPress={() => addToCart(item)}
            >
              <Text style={styles.addToCartButtonText}>Add to Cart</Text>
            </TouchableOpacity>
          </View>
        ))}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 19,
    paddingHorizontal: 20,
    justifyContent: 'center',
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 10,
  },
  foodItemsContainer: {
    flex: 1,
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginTop: 20,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
    width: '100%',
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  addToCartButton: {
    backgroundColor: '#007AFF',
    padding: 10,
    borderRadius: 5,
    marginTop: 5,
  },
  addToCartButtonText: {
    color: 'white',
    fontSize: 14,
    textAlign: 'center',
  },
});
