import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome'; // Import the cart icon

export default function Dashboard({ navigation }) {
  // Restaurant name and menu items
  const restaurantName = 'Delicious Delights'; // Restaurant name
  const menuItems = [
    { name: 'Spaghetti Carbonara', price: '$12.99' },
    { name: 'Margherita Pizza', price: '$10.99' },
    { name: 'Chicken Alfredo', price: '$14.99' },
    { name: 'Caesar Salad', price: '$8.99' },
    { name: 'Chocolate Brownie Sundae', price: '$6.99' },
    { name: 'Iced Cappuccino', price: '$4.99' },
    // Add more menu items as needed
  ];

  return (
    <Background>
      <View style={styles.headerContainer}>
        <Logo />
        <Header>Welcome to {restaurantName}</Header>
      </View>

      <ScrollView style={styles.menuContainer}>
        <Text style={styles.sectionTitle}>Menu</Text>
        {menuItems.map((item, index) => (
          <TouchableOpacity
            key={index}
            style={styles.menuItem}
            onPress={() => {
              // Handle item selection, e.g., navigate to item details screen
              navigation.navigate('MenuItemDetails', { item });
            }}
          >
            <Text style={styles.menuItemName}>{item.name}</Text>
            <Text style={styles.menuItemPrice}>{item.price}</Text>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {/* Cart icon */}
      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>

    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  menuContainer: {
    flex: 1,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  menuItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
  },
  menuItemName: {
    fontSize: 18,
  },
  menuItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  cartButton: {
    backgroundColor: 'blue', 
    opacity:0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
