import React, { useState, useEffect } from 'react';
import { TouchableOpacity, StyleSheet, Text, View, ScrollView, Button, Alert, ActivityIndicator, Modal } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { useCart } from '../../CartContext';
import Icon from 'react-native-vector-icons/FontAwesome';

// Assuming you have a user context for authentication
import { useUser } from '../../UserContext';

export default function CartIcon() {
  const navigation = useNavigation();
  const { cartItems, removeFromCart, resetCart, clearCart } = useCart();
  const [isLoading, setIsLoading] = useState(false);
  const [isOrderOnTheWay, setIsOrderOnTheWay] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);

  // Get the authenticated user's information, including their user ID
  const { user } = useUser();
  const userId = user.id;

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
  };

  const handleBuy = () => {
    if (cartItems.length > 0) {
      setIsLoading(true);

      // Send a request to your API to update the user's cart
      updateUserCart(userId, cartItems)
        .then(() => {
          setIsLoading(false);
          setIsOrderOnTheWay(true);
          clearCart();
          setShowSuccessModal(true);

          setTimeout(() => {
            setShowSuccessModal(false);
          }, 2000);
        })
        .catch((error) => {
          console.error('Error updating cart:', error);
          setIsLoading(false);
        });
    } else {
      Alert.alert('Empty Cart', 'Your cart is empty. Please add items to your cart before purchasing.');
    }
  }

  return (
    <>
      <TouchableOpacity
        onPress={() => navigation.navigate('Dashboard')}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>

      {cartItems.length > 0 ? (
        <ScrollView style={styles.cartItemsContainer}>
          <View style={styles.cartTitle}>
            <Text style={styles.cartTitleText}>CART</Text>
            <View style={styles.cartTitleLine}></View>
          </View>
          {cartItems.map((item, index) => (
            <View key={index} style={styles.cartItem}>
              <Text style={styles.cartItemName}>{item.name}</Text>
              <Text style={styles.cartItemPrice}>{item.price}</Text>
              <TouchableOpacity onPress={() => removeFromCart(item)} style={styles.removeButton}>
                <Text style={styles.removeButtonText}>Remove</Text>
              </TouchableOpacity>
            </View>
          ))}
          <Text style={styles.cartTotal}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
          {isLoading ? (
            <ActivityIndicator size="large" color="#007BFF" />
          ) : isOrderOnTheWay ? (
            <Text style={styles.orderOnTheWayText}>Your order is on the way!</Text>
          ) : (
            <Button title="Buy" onPress={handleBuy} />
          )}
        </ScrollView>
      ) : (
        <Text style={styles.emptyCartText}>Your cart is empty.</Text>
      )}

      <Modal
        animationType="fade"
        transparent={true}
        visible={showSuccessModal}
        onRequestClose={() => {
          setShowSuccessModal(false);
        }}
      >
        <View style={styles.modalContainer}>
          <Text style={styles.successModalText}>Your order is on the way!</Text>
        </View>
      </Modal>
    </>
  );
}

// Replace this function with an actual API request to update the user's cart
async function updateUserCart(userId, cartItems) {
  try {
    // Make an API request to update the user's cart on the server
    const response = await fetch(`your_api_endpoint/updateCart/${userId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ cartItems }),
    });

    if (response.status === 200) {
      // Cart update was successful
      return true;
    } else {
      // Handle any errors from the server
      throw new Error('Cart update failed');
    }
  } catch (error) {
    throw error;
  }
}

// The rest of your styles remain the same
const styles = StyleSheet.create({
  // ...
});
