import React, { createContext, useContext, useState } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const [currentUser, setCurrentUser] = useState(null); // User identifier

  const sellerEmail = 'seller@gmail.com'; // Replace with the seller's email

  const addToCart = (item) => {
    // Check if the current user is the seller
    if (currentUser === sellerEmail) {
      setCartItems([...cartItems, item]);
    } else {
      console.error('Access denied. You are not the seller.');
    }
  };

  const removeFromCart = (item) => {
    // Check if the current user is the seller
    if (currentUser === sellerEmail) {
      // Find the first item with the specified name and remove it
      const indexOfItemToRemove = cartItems.findIndex((cartItem) => cartItem.name === item.name);
      if (indexOfItemToRemove !== -1) {
        const updatedCart = [...cartItems];
        updatedCart.splice(indexOfItemToRemove, 1);
        setCartItems(updatedCart);
      }
    } else {
      console.error('Access denied. You are not the seller.');
    }
  };

  const clearCart = () => {
    // Check if the current user is the seller
    if (currentUser === sellerEmail) {
      setCartItems([]);
    } else {
      console.error('Access denied. You are not the seller.');
    }
  };

  const loginUser = (user) => {
    setCurrentUser(user);
  };

  const logoutUser = () => {
    setCurrentUser(null);
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart, loginUser, logoutUser }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
