import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Modal, TextInput, Button, ScrollView } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useNavigation } from '@react-navigation/native';

export default function Seller() {
  const navigation = useNavigation();
  const [isModalVisible, setModalVisible] = useState(false);
  const [fastFoodMenu, setFastFoodMenu] = useState('');
  const [fastFoodPrice, setFastFoodPrice] = useState('');
  const [postedFastFoods, setPostedFastFoods] = useState([]);
  const [editablePlusButton, setEditablePlusButton] = useState(false);
  const [plusButtonText, setPlusButtonText] = useState('Post a Fast Food');

  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  const togglePlusButtonEdit = () => {
    setEditablePlusButton(!editablePlusButton);
  };

  const handlePlusButtonPress = () => {
    if (editablePlusButton) {
      // Save the edited text
      setPlusButtonText(fastFoodMenu);
    }
    togglePlusButtonEdit();
  };

  const handleSearch = () => {
    // Check if fastFoodPrice is a valid number
    if (isNaN(fastFoodPrice)) {
      alert("Please enter a valid number for the price.");
      return;
    }

    // Create a new array with the existing fast food items and add the new one
    const newItem = {
      menu: fastFoodMenu,
      price: fastFoodPrice,
    };
    const updatedFastFoods = [...postedFastFoods, newItem];
    setPostedFastFoods(updatedFastFoods);

    // Clear the input fields
    setFastFoodMenu('');
    setFastFoodPrice('');

    // Close the modal
    toggleModal();
  };

  const handleDelete = (index) => {
    const updatedFastFoods = [...postedFastFoods];
    updatedFastFoods.splice(index, 1);
    setPostedFastFoods(updatedFastFoods);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>

      <Text style={styles.sectionTitle}>Seller</Text>

      <TouchableOpacity onPress={toggleModal} style={styles.button}>
        <Text style={styles.buttonText}>
          {editablePlusButton ? plusButtonText : 'Post a Fast Food'}
        </Text>
      </TouchableOpacity>

      {editablePlusButton && (
        <TouchableOpacity onPress={handlePlusButtonPress} style={styles.editButton}>
          <Text style={styles.buttonText}>
            {editablePlusButton ? 'Save' : 'Edit'}
          </Text>
        </TouchableOpacity>
      )}

      <ScrollView style={styles.fastFoodList}>
        {postedFastFoods.map((item, index) => (
          <View key={index} style={styles.fastFoodItem}>
            <Text style={styles.foodItemMenu}>{item.menu}</Text>
            <Text style={styles.foodItemPrice}>Price: {item.price}</Text>
            <TouchableOpacity
              style={styles.deleteButton}
              onPress={() => handleDelete(index)}
            >
              <Text style={styles.deleteButtonText}>Delete</Text>
            </TouchableOpacity>
          </View>
        ))}
      </ScrollView>

      <Modal
        animationType="slide"
        transparent={true}
        visible={isModalVisible}
      >
        <View style={styles.modalContainer}>
          <Text style={styles.modalTitle}>Post Fast Food</Text>
          <TextInput
            placeholder="Fast Food Menu"
            onChangeText={(text) => setFastFoodMenu(text)}
            value={fastFoodMenu}
            style={styles.input}
            editable={editablePlusButton}
          />
          <TextInput
            placeholder="Fast Food Price"
            onChangeText={(text) => setFastFoodPrice(text)}
            value={fastFoodPrice}
            style={styles.input}
            keyboardType="numeric" // This sets the keyboard to numeric
          />
          <Button title="Post" onPress={handleSearch} />
          <Button title="Close" onPress={toggleModal} />
        </View>
      </Modal>
    </View>
  );
}


