import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated, TouchableWithoutFeedback, ScrollView, Alert } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';
import ShoppingCart from './ShoppingCart';

export default function Dashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));

  const toggleProfileIcon = () => {
    // Assuming you have the necessary 'state' object defined somewhere
    // Replace 'state.username' with the actual value you want to use
    const username = state.username;

    // Assuming you are making an API request, replace this with your actual API call
    // This is a placeholder for illustration purposes
    fetch('your-api-endpoint-here')
      .then((response) => response.json())
      .then((data) => {
        if (data.status === 'success') {
          navigation.navigate('ProfileIcon', { username });
        } else {
          Alert.alert('Email and password do not match.');
        }
        console.log('response', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const toggleShoppingCart = () => {
    navigation.navigate('ShoppingCart');
  };

  const handlePress = (menu) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      if (menu === 'McDonalds') {
        navigation.navigate(menu + 'Menu');
      } else if (menu === 'Subway') {
        navigation.navigate(menu);
      } else if (menu === 'KFC') {
        navigation.navigate(menu);
      }
    });
  };

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <Background>
        <Logo />
        <View style={styles.headerContainer}>
          <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
        </View>

        {/* Profile Icon TouchableOpacity */}
        <TouchableOpacity style={styles.profileButton} onPress={toggleProfileIcon}>
          <Icon name="user" size={30} color="#007BFF" />
        </TouchableOpacity>

        {/* McDonald's */}
        <TouchableWithoutFeedback onPress={() => handlePress('McDonalds')}>
          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>McDonald's</Text>
              <Text style={styles.salesQuote}>
                "I'm lovin' it!" - McDonald's
              </Text>
              <Text style={styles.additionalInfo}>
                Serving Happiness Since 1955
              </Text>
              <Text style={styles.additionalInfo}>
                Find us at 1234 Fast Food Lane
              </Text>
            </View>
            <Icon name="arrow-right" size={30} color="#007BFF" />
          </Animated.View>
        </TouchableWithoutFeedback>

        {/* Rest of your code remains the same */}
      </Background>
    </ScrollView>
  );
}


const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.2)', 
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 10,
    borderRadius: 8,
    marginBottom: 20,
    height: 180,
    width: 300, 
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF', 
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  additionalInfo: {
    fontSize: 16,
    textAlign: 'center',
    marginTop: 10,
    color: '#333',
  },
  cartButton: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  profileButton: {
    position: 'absolute',
    top: 20,
    left: 20,
  },
});
