import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import { createRoot } from 'react-dom';
import Icon from 'react-native-vector-icons/FontAwesome'; // Import the cart icon


export default function Dashboard({ navigation }) {
  // Brand names and menu items
  const fastFoodBrands = [
    {
      name: 'McDonald\'s',
      icon: <View style={styles.emptyBox} />, // Use an empty box
      menuItems: [
        { name: 'Big Mac', price: '$5.99' },
        { name: 'Chicken McNuggets', price: '$4.99' },
        { name: 'Quarter Pounder', price: '$6.99' },
        { name: 'French Fries', price: '$2.99' },
      ],
    },
    {
      name: 'Krispy Kreme',
      icon: <View style={styles.emptyCircle} />, // Use an empty circle
      menuItems: [
        { name: 'Original Glazed Doughnut', price: '$1.49' },
        { name: 'Chocolate Iced Doughnut', price: '$1.99' },
        { name: 'Coffee', price: '$2.49' },
        { name: 'Assorted Dozen Doughnuts', price: '$12.99' },
      ],
    },
    // Add more brands and menu items as needed
  ];

  return (
    <Background>
      <View style={styles.headerContainer}>
        <Logo />
        <Header>Welcome to FastFood Express</Header>
      </View>

      <ScrollView style={styles.foodItemsContainer}>
        <Text style={styles.sectionTitle}>Menu</Text>
        {fastFoodBrands.map((brand, brandIndex) => (
          <View key={brandIndex}>
            <View style={styles.brandHeader}>
              <Text style={styles.brandName}>{brand.name}</Text>
              {brand.icon} {/* Display the empty box or circle */}
            </View>
            {brand.menuItems.map((item, itemIndex) => (
              <TouchableOpacity
                key={itemIndex}
                style={styles.foodItem}
                onPress={() => {
                  // Handle item selection, e.g., navigate to item details screen
                  navigation.navigate('FoodItemDetails', { item });
                }}
              >
                <Text style={styles.foodItemName}>{item.name}</Text>
                <Text style={styles.foodItemPrice}>{item.price}</Text>
              </TouchableOpacity>
            ))}
          </View>
        ))}
      </ScrollView>

      {/* Cart icon */}
      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>

    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  foodItemsContainer: {
    flex: 1,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  brandHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  brandName: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  emptyBox: {
    width: 30,
    height: 30,
    backgroundColor: 'transparent', // Set the background color to make it an empty box
    borderWidth: 2,
    borderColor: 'black', // Border color for the box
  },
  emptyCircle: {
    width: 30,
    height: 30,
    backgroundColor: 'transparent', // Set the background color to make it an empty circle
    borderRadius: 15, // Half of the width/height to make it a circle
    borderWidth: 2,
    borderColor: 'black', // Border color for the circle
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  cartButton: {
    backgroundColor: 'blue', 
    opacity: 0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
