import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Animated,
  TouchableWithoutFeedback,
  ScrollView,
  BackHandler,
  TextInput,
} from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function SDashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));
  const [editablePlusButton, setEditablePlusButton] = useState(false);
  const [plusButtonText, setPlusButtonText] = useState('+');

  const toggleSProfileIcon = () => {
    navigation.navigate('SProfileIcon');
  };

  const toggleSCartIcon = () => {
    navigation.navigate('SCartIcon');
  };

  const handlePlusButtonPress = () => {
    if (editablePlusButton) {
      // Save the edited text
      setPlusButtonText(plusButtonText);
    }
    setEditablePlusButton(!editablePlusButton);
  };

  const handlePress = (menu) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      if (menu === 'SMcDonalds') {
        navigation.navigate(menu + 'Menu');
      } else if (menu === 'SSubway') {
        navigation.navigate(menu);
      } else if (menu === 'SKFC') {
        navigation.navigate(menu);
      } else if (menu === 'Seller') {
        navigation.navigate(menu);
      }
    });
  };

  const cartItems = [];

  useEffect(() => {
    const backAction = () => {
      if (navigation.isFocused()) {
        return true;
      }
      return false;
    };

    const backHandler = BackHandler.addEventListener('hardwareBackPress', backAction);

    return () => backHandler.remove();
  }, [navigation]);

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <Background>
        <Logo />
        <View style={styles.headerContainer}>
          <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
          <TouchableOpacity style={styles.plusButton} onPress={handlePlusButtonPress}>
            {editablePlusButton ? (
              <TextInput
                style={styles.plusButtonText}
                onChangeText={(text) => setPlusButtonText(text)}
                value={plusButtonText}
              />
            ) : (
              <Text style={styles.plusButtonText}>{plusButtonText}</Text>
            )}
          </TouchableOpacity>
        </View>

        <TouchableOpacity style={styles.profileButton} onPress={toggleSProfileIcon}>
          <View style={styles.iconCircle}>
            <Icon name="user" size={30} color="#FFF" />
          </View>
        </TouchableOpacity>

        <TouchableWithoutFeedback onPress={() => handlePress('Seller')}>
          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText1}>+</Text>
            </View>
            <Icon name="arrow-right" size={30} color="#007BFF" />
          </Animated.View>
        </TouchableWithoutFeedback>
        <TouchableWithoutFeedback onPress={() => handlePress('SMcDonalds')}>
          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>McDonald's</Text>
              <Text style={styles.salesQuote}> "I'm lovin' it!" - McDonald's</Text>
              <Text style={styles.additionalInfo}>Serving Happiness Since 1955</Text>
              <Text style={styles.additionalInfo}>Find us at 1234 Fast Food Lane</Text>
            </View>
            <Icon name="arrow-right" size={30} color="#007BFF" />
          </Animated.View>
        </TouchableWithoutFeedback>

        <TouchableWithoutFeedback onPress={() => handlePress('SSubway')}>
          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>Subway</Text>
              <Text style={styles.salesQuote}> "Eat Fresh!" - Subway</Text>
              <Text style={styles.additionalInfo}>Fresh Subs and Sandwiches</Text>
              <Text style={styles.additionalInfo}>Visit us at 8765 Sub Avenue</Text>
            </View>
            <Icon name="arrow-right" size={30} color="#007BFF" />
          </Animated.View>
        </TouchableWithoutFeedback>

        <TouchableWithoutFeedback onPress={() => handlePress('SKFC')}>
          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>KFC</Text>
              <Text style={styles.salesQuote}> "Finger Lickin' Good!" - KFC</Text>
              <Text style={styles.additionalInfo}>Original Recipe Chicken since 1939</Text>
              <Text style={styles.additionalInfo}>Visit us at 4321 Fried Chicken Street</Text>
            </View>
            <Icon name="arrow-right" size={30} color="#007BFF" />
          </Animated.View>
        </TouchableWithoutFeedback>

        <TouchableOpacity style={styles.cartButton} onPress={toggleSCartIcon}>
          <View style={styles.iconCircle}>
            <Text style={styles.cartIcon}>🛒</Text>
            {cartItems.length > 0 && (
              <Text style={styles.cartItemCount}>{cartItems.length}</Text>
            )}
          </View>
        </TouchableOpacity>
      </Background>
    </ScrollView>
  );
}


