          shadowOffset: { width: 4, height: 5 },
          shadowOpacity: 0.5,
          shadowRadius: 5, 
          elevation: 1,
        },
          brandButton: {
          backgroundColor: 'rgba(255, 255, 255, 0.2)',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: 10,
          borderRadius: 1.1,
          marginBottom: 20,
          height: 180,
          width: 300,
          borderWidth: 0.1, 
          shadowOffset: { width: 4, height: 5 },
          shadowOpacity: 0.5,
          shadowRadius: 5, 
          elevation: 1,
        },
        brandButtonText: {
          fontSize: 24,
          fontWeight: 'bold',
          color: '#007BFF', 
        },
        brandButtonText1: {
          fontSize: 90,
          fontWeight: 'bold',
          color: '#007BFF',
          textAlign: 'center', // Center align the text horizontally
          marginLeft:'100px',
        },
        salesQuote: {
          fontSize: 18,
          fontStyle: 'italic',
          textAlign: 'center',
        },
        additionalInfo: {
          fontSize: 16,
          textAlign: 'center',
          marginTop: 10,
          color: '#333',
        },
        cartButton: {
          position: 'absolute',
          top: 50,
          right: 20,
          backgroundColor: 'transparent',
        },
        cartIcon: {
          fontSize: 24,
        },
        cartItemCount: {
          position: 'absolute',
          top: 0,
          right: 0,
          backgroundColor: 'red',
          color: 'white',
          borderRadius: 10,
          paddingHorizontal: 4, 
          paddingVertical: 2,
          fontSize: 10,
        },
        profileButton: {
          position: 'absolute',
          top: 50,
          left: 20,
        },
        iconCircle: {
          width: 60,
          height: 60,
          borderRadius: 30,
          backgroundColor: '#C7E8CA', 
          justifyContent: 'center',
          alignItems: 'center',
        },
      });
