import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome'; // Import the cart icon
import ReactDOM from 'react-dom';

const Dashboard = ({ navigation }) => {
  // Brand names and menu items
  const fastFoodBrands = [
    {
      name: "McDonald's",
      icon: <View style={styles.emptyBox} />, // Use an empty box
      menuItems: [
        { name: 'Big Mac', price: '$5.99' },
        { name: 'Chicken McNuggets', price: '$4.99' },
        { name: 'Quarter Pounder', price: '$6.99' },
        { name: 'French Fries', price: '$2.99' },
      ],
    },
    {
      name: 'Krispy Kreme',
      icon: <View style={styles.emptyCircle} />, // Use an empty circle
      menuItems: [
        { name: 'Original Glazed Doughnut', price: '$1.49' },
        { name: 'Chocolate Iced Doughnut', price: '$1.99' },
        { name: 'Coffee', price: '$2.49' },
        { name: 'Assorted Dozen Doughnuts', price: '$12.99' },
      ],
    },
    // Add more brands and menu items as needed
  ];

  ReactDOM.render(
    <Background>
      <View style={styles.headerContainer}>
        <Logo />
        <Header>Welcome to FastFood Express</Header>
      </View>

      <ScrollView style={styles.foodItemsContainer}>
        <Text style={styles.sectionTitle}>Menu</Text>
        {fastFoodBrands.map((brand, brandIndex) => (
          <View key={brandIndex}>
            <View style={styles.brandHeader}>
              <Text style={styles.brandName}>{brand.name}</Text>
              {brand.icon} {/* Display the empty box or circle */}
            </View>
            {brand.menuItems.map((item, itemIndex) => (
              <TouchableOpacity
                key={itemIndex}
                style={styles.foodItem}
                onPress={() => {
                  // Handle item selection, e.g., navigate to item details screen
                  navigation.navigate('FoodItemDetails', { item });
                }}
              >
                <Text style={styles.foodItemName}>{item.name}</Text>
                <Text style={styles.foodItemPrice}>{item.price}</Text>
              </TouchableOpacity>
            ))}
          </View>
        ))}
      </ScrollView>

      {/* Cart icon */}
      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>
    </Background>,
    document.getElementById('root')
  );
};

const styles = StyleSheet.create({
  // Your existing styles here
});

export default Dashboard;
