import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, StyleSheet, TouchableOpacity, Animated, TouchableWithoutFeedback, ScrollView, BackHandler } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';

const menuItems = [
  {
    name: "Seller",
    text: "+",
    description: "Click me to edit",
  },
  {
    name: "SMcDonalds",
    text: "McDonald's",
    quote: "\"I'm lovin' it!\" - McDonald's",
    info1: "Serving Happiness Since 1955",
    info2: "Find us at 1234 Fast Food Lane",
  },
  {
    name: "SSubway",
    text: "Subway",
    quote: "\"Eat Fresh!\" - Subway",
    info1: "Fresh Subs and Sandwiches",
    info2: "Visit us at 8765 Sub Avenue",
  },
  {
    name: "SKFC",
    text: "KFC",
    quote: "\"Finger Lickin' Good!\" - KFC",
    info1: "Original Recipe Chicken since 1939",
    info2: "Visit us at 4321 Fried Chicken Street",
  },
];

export default function SDashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));
  const [editablePlusButton, setEditablePlusButton] = useState(false);
  const [plusButtonText, setPlusButtonText] = useState("Click me to edit");

  const toggleSProfileIcon = () => {
    navigation.navigate('SProfileIcon');
  };

  const toggleSCartIcon = () => {
    navigation.navigate('SCartIcon');
  };

  const handlePlusButtonPress = () => {
    setEditablePlusButton(!editablePlusButton);
  };

  const handlePress = (menu) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      if (menu === 'Seller') {
        // Handle special case for editing the button text
        if (editablePlusButton) {
          setPlusButtonText(plusButtonText); // Update with the new text here
        }
      } else {
        navigation.navigate(menu);
      }
    });
  };

  const cartItems = [];

  useEffect(() => {
    const backAction = () => {
      if (navigation.isFocused()) {
        return true;
      }
      return false;
    };

    const backHandler = BackHandler.addEventListener('hardwareBackPress', backAction);

    return () => backHandler.remove();
  }, [navigation]);

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <Background>
        <Logo />
        <View style={styles.headerContainer}>
          <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
          <TouchableOpacity style={styles.plusButton} onPress={handlePlusButtonPress}>
            {editablePlusButton ? (
              <TextInput
                style={styles.editableButtonText}
                onChangeText={text => setPlusButtonText(text)}
                value={plusButtonText}
              />
            ) : (
              <Text style={styles.brandButtonText1}>{plusButtonText}</Text>
            )}
          </TouchableOpacity>
        </View>

        <TouchableOpacity style={styles.profileButton} onPress={toggleSProfileIcon}>
          <View style={styles.iconCircle}>
            <Icon name="user" size={30} color="#FFF" />
          </View>
        </TouchableOpacity>

        {menuItems.map((menuItem) => (
          <TouchableWithoutFeedback key={menuItem.name} onPress={() => handlePress(menuItem.name)}>
            <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
              <View>
                <Text style={styles.brandButtonText}>{menuItem.text}</Text>
                {menuItem.quote && <Text style={styles.salesQuote}>{menuItem.quote}</Text>}
                {menuItem.info1 && <Text style={styles.additionalInfo}>{menuItem.info1}</Text>}
                {menuItem.info2 && <Text style={styles.additionalInfo}>{menuItem.info2}</Text>}
              </View>
              <Icon name="arrow-right" size={30} color="#007BFF" />
            </Animated.View>
          </TouchableWithoutFeedback>
        ))}

        <TouchableOpacity style={styles.cartButton} onPress={toggleSCartIcon}>
          <View style={styles.iconCircle}>
            <Text style={styles.cartIcon}>ðŸ›’</Text>
            {cartItems.length > 0 && (
              <Text style={styles.cartItemCount}>{cartItems.length}</Text>
            )}
          </View>
        </TouchableOpacity>
      </Background>
    </ScrollView>
  );
}
const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 10,
    borderRadius: 1.1,
    marginBottom: 20,
    height: 180,
    width: 300,
    borderWidth: 0.1,
    shadowOffset: { width: 4, height: 5 },
    shadowOpacity: 0.5,
    shadowRadius: 5,
    elevation: 1,
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF',
  },
  brandButtonText1: {
    fontSize: 90,
    fontWeight: 'bold',
    color: '#007BFF',
    textAlign: 'center',
    marginLeft: 100,
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  additionalInfo: {
    fontSize: 16,
    textAlign: 'center',
    marginTop: 10,
    color: '#333',
  },
  cartButton: {
    position: 'absolute',
    top: 50,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartItemCount: {
    position: 'absolute',
    top: 0,
    right: 0,
    backgroundColor: 'red',
    color: 'white',
    borderRadius: 10,
    paddingHorizontal: 4,
    paddingVertical: 2,
    fontSize: 10,
  },
  profileButton: {
    position: 'absolute',
    top: 50,
    left: 20,
  },
  iconCircle: {
    width: 60,
    height: 60,
    borderRadius: 30,
    backgroundColor: '#C7E8CA',
    justifyContent: 'center',
    alignItems: 'center',
  },
  editableButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF',
    textAlign: 'center',
  },
  plusButton: {
    borderWidth: 1,
    borderColor: '#007BFF',
    borderRadius: 5,
    padding: 10,
    marginBottom: 20,
  },
});
