import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableHighlight, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome'; // Import the cart icon
import { useNavigation } from '@react-navigation/native'; // Import the navigation hook

export default function KFCMenu() {
  const navigation = useNavigation(); // Initialize navigation

  const menuItems = [
    { name: 'Original Recipe Chicken', price: '$8.99' },
    { name: 'Chicken Tenders', price: '$7.49' },
    { name: 'Zinger Burger', price: '$6.99' },
    { name: 'Mashed Potatoes', price: '$2.99' },
    // Add more KFC menu items with prices as needed
  ];

  const [pressedMenuItem, setPressedMenuItem] = useState(null);

  const toggleCartIcon = () => {
    navigation.navigate('CartIcon');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.sectionTitle}>KFC Menu</Text>
      <ScrollView
        style={styles.foodItemsContainer}
        stickyHeaderIndices={[0]} // Make the first child (sectionTitle) sticky
      >
        <View style={styles.stickyHeader}>
          {/* Cart icon in the header */}
          <TouchableOpacity style={styles.cartButton} onPress={toggleCartIcon}>
            <Text style={styles.cartIcon}>ðŸ›’</Text>
          </TouchableOpacity>
        </View>

        {menuItems.map((item, itemIndex) => (
          <TouchableHighlight
            key={itemIndex}
            style={[
              styles.foodItem,
              { backgroundColor: pressedMenuItem === item.name ? '#FFD700' : '#fff' },
            ]}
            underlayColor="#FFD700"
            onPress={() => {
              navigation.navigate('FoodItemDetails', { item });
            }}
            onShowUnderlay={() => setPressedMenuItem(item.name)}
            onHideUnderlay={() => setPressedMenuItem(null)}
          >
            <>
              <Text style={styles.foodItemName}>{item.name}</Text>
              <Text style={styles.foodItemPrice}>{item.price}</Text>
            </>
          </TouchableHighlight>
        ))}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 10,
  },
  foodItemsContainer: {
    flex: 1,
  },
  stickyHeader: {
    backgroundColor: 'white',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1,
    elevation: 2,
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
    width: '100%', // Ensure full width for centered items
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartButton: {
    backgroundColor: 'transparent',
  },
});
