import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  StyleSheet,
  TouchableOpacity,
  Animated,
  TouchableWithoutFeedback,
  ScrollView,
  BackHandler,
  Linking,
} from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function SDashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));
  const [showTextFields, setShowTextFields] = useState(false);
  const [fastFoodName, setFastFoodName] = useState('');
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [menuItems, setMenuItems] = useState([
    {
      id: 1,
      brand: 'McDonald\'s',
      salesQuote: '"I\'m lovin\' it!" - McDonald\'s',
      additionalInfo: 'Serving Happiness Since 1955\nFind us at 1234 Fast Food Lane',
    },
    {
      id: 2,
      brand: 'Subway',
      salesQuote: '"Eat Fresh!" - Subway',
      additionalInfo: 'Fresh Subs and Sandwiches\nVisit us at 8765 Sub Avenue',
    },
    {
      id: 3,
      brand: 'KFC',
      salesQuote: '"Finger Lickin\' Good!" - KFC',
      additionalInfo: 'Original Recipe Chicken since 1939\nVisit us at 4321 Fried Chicken Street',
    },
  ]);

  const toggleSProfileIcon = () => {
    navigation.navigate('SProfileIcon');
  };

  const toggleSCartIcon = () => {
    navigation.navigate('SCartIcon');
  };

  const handlePlusButtonPress = () => {
    setShowTextFields(true);
  };

  const handleDoneButtonPress = () => {
    const newItem = {
      id: menuItems.length + 1,
      brand: fastFoodName,
      salesQuote: `"${productName}"`,
      additionalInfo: `Price: $${productPrice}`,
    };

    setMenuItems([...menuItems, newItem]);

    setFastFoodName('');
    setProductName('');
    setProductPrice('');
    setShowTextFields(false);
  };

  const handleEditButtonPress = (id) => {
    // Implement editing logic here
    // You can open a modal or a new screen for editing
    // For now, we'll just show an alert
    alert(`Edit item with ID: ${id}`);
  };

  const handleDeleteButtonPress = (id) => {
    // Implement delete logic here
    // Remove the item with the given ID from the menuItems
    const updatedMenuItems = menuItems.filter((item) => item.id !== id);
    setMenuItems(updatedMenuItems);
  };

  const handlePress = (menuItem) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      // Open the link in a web browser
      // Linking.openURL(menuItem.link);
    });
  };

  const cartItems = [];

  useEffect(() => {
    const backAction = () => {
      if (navigation.isFocused()) {
        return true;
      }
      return false;
    };

    const backHandler = BackHandler.addEventListener('hardwareBackPress', backAction);

    return () => backHandler.remove();
  }, [navigation]);

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <Background>
        <Logo />
        <View style={styles.headerContainer}>
          <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
          <TouchableOpacity style={styles.plusButton} onPress={handlePlusButtonPress}>
            <View style={styles.iconCircle}>
              <Text style={styles.plusIcon}>+</Text>
            </View>
          </TouchableOpacity>
        </View>

        <TouchableOpacity style={styles.profileButton} onPress={toggleSProfileIcon}>
          <View style={styles.iconCircle}>
            <Icon name="user" size={30} color="#FFF" />
          </View>
        </TouchableOpacity>

        {menuItems.map((menuItem) => (
          <View key={menuItem.id}>
            <TouchableWithoutFeedback onPress={() => handlePress(menuItem)}>
              <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
                <View>
                  <Text style={styles.brandButtonText}>{menuItem.brand}</Text>
                  <Text style={styles.salesQuote}>{menuItem.salesQuote}</Text>
                  <Text style={styles.additionalInfo}>{menuItem.additionalInfo}</Text>
                </View>
                <View style={styles.itemActions}>
                  <TouchableOpacity
                    style={styles.editButton}
                    onPress={() => handleEditButtonPress(menuItem.id)}
                  >
                    <Icon name="edit" size={20} color="#007BFF" />
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={styles.deleteButton}
                    onPress={() => handleDeleteButtonPress(menuItem.id)}
                  >
                    <Icon name="trash" size={20} color="#FF0000" />
                  </TouchableOpacity>
                </View>
                <Icon name="arrow-right" size={30} color="#007BFF" />
              </Animated.View>
            </TouchableWithoutFeedback>
          </View>
        ))}

        {showTextFields && (
          <View style={styles.textFields}>
            <TextInput
              style={styles.textField}
              placeholder="Fast Food Name"
              value={fastFoodName}
              onChangeText={(text) => setFastFoodName(text)}
            />
            <TextInput
              style={styles.textField}
              placeholder="Product Name"
              value={productName}
              onChangeText={(text) => setProductName(text)}
            />
            <TextInput
              style={styles.textField}
              placeholder="Product Price"
              value={productPrice}
              onChangeText={(text) => setProductPrice(text)}
              keyboardType="numeric"
            />
            <TouchableOpacity style={styles.doneButton} onPress={handleDoneButtonPress}>
              <Text style={styles.doneButtonText}>Done</Text>
            </TouchableOpacity>
          </View>
        )}

        <TouchableOpacity style={styles.cartButton} onPress={toggleSCartIcon}>
          <View style={styles.iconCircle}>
            <Text style={styles.cartIcon}>ðŸ›’</Text>
            {cartItems.length > 0 && (
              <Text style={styles.cartItemCount}>{cartItems.length}</Text>
            )}
          </View>
        </TouchableOpacity>
      </Background>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  plusButton: {
    marginTop: 10,
    alignItems: 'left',
    marginRight: 240,
  },
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 10,
    borderRadius: 1.1,
    marginBottom: 20,
    height: 180,
    width: 300,
    borderWidth: 0.1,
    shadowOffset: { width: 4, height: 5 },
    shadowOpacity: 0.5,
    shadowRadius: 5,
    elevation: 1,
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF',
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  additionalInfo: {
    fontSize: 16,
    textAlign: 'center',
    marginTop: 10,
    color: '#333',
  },
  cartButton: {
    position: 'absolute',
    top: 50,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartItemCount: {
    position: 'absolute',
    top: 0,
    right: 0,
    backgroundColor: 'red',
    color: 'white',
    borderRadius: 10,
    paddingHorizontal: 4,
    paddingVertical: 2,
    fontSize: 10,
  },
  profileButton: {
    position: 'absolute',
    top: 50,
    left: 20,
  },
  iconCircle: {
    width: 60,
    height: 60,
    borderRadius: 30,
    backgroundColor: '#C7E8CA',
    justifyContent: 'center',
    alignItems: 'center',
  },
  itemActions: {
    flexDirection: 'row',
  },
  editButton: {
    marginRight: 10,
  },
  deleteButton: {},
  textFields: {
    padding: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    marginBottom: 20,
  },
  textField: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  doneButton: {
    backgroundColor: '#007BFF',
    paddingVertical: 10,
    borderRadius: 5,
    alignItems: 'center',
  },
  doneButtonText: {
    color: 'white',
    fontSize: 16,
  },
});
