import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableHighlight } from 'react-native';

export default function SubwayMenu({ navigation }) {
  const menuItems = [
    { name: 'Italian BMT', price: '$6.99' },
    { name: 'Chicken Teriyaki', price: '$7.49' },
    { name: 'Veggie Delite', price: '$5.99' },
  ];

  const [pressedMenuItem, setPressedMenuItem] = useState(null);
  const toggleCartIcon = () => {
    setCartVisible(!cartVisible);
  };

  return (
    <ScrollView style={styles.foodItemsContainer}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>
      <TouchableOpacity style={styles.cartButton} onPress={toggleCartIcon}>
        <Text style={styles.cartIcon}>ðŸ›’</Text>
      </TouchableOpacity>
      <Text style={styles.sectionTitle}>Subway Menu</Text>
      {menuItems.map((item, itemIndex) => (
        <TouchableHighlight
          key={itemIndex}
          style={[
            styles.foodItem,
            { backgroundColor: pressedMenuItem === item.name ? '#FFD700' : '#fff' },
          ]}
          underlayColor="#FFD700"
          onPress={() => {
            navigation.navigate('FoodItemDetails', { item });
          }}
          onShowUnderlay={() => setPressedMenuItem(item.name)}
          onHideUnderlay={() => setPressedMenuItem(null)}
        >
          <>
            <Text style={styles.foodItemName}>{item.name}</Text>
            <Text style={styles.foodItemPrice}>{item.price}</Text>
          </>
        </TouchableHighlight>
      ))}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  foodItemsContainer: {
    flex: 1,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
});
