import React, { createContext, useContext, useState } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  // Retrieve cart data from localStorage based on the user's identifier
  const userId = getAuthenticatedUserId(); // Replace this with your authentication logic
  const initialCart = localStorage.getItem(`userCart_${userId}`) || '[]';
  const [cartItems, setCartItems] = useState(JSON.parse(initialCart));

  const addToCart = (item) => {
    setCartItems((prevCart) => [...prevCart, item]);
  };

  const removeFromCart = (item) => {
    setCartItems((prevCart) => prevCart.filter((cartItem) => cartItem.name !== item.name));
  };

  const clearCart = () => {
    setCartItems([]);
  };

  // Save the user-specific cart data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem(`userCart_${userId}`, JSON.stringify(cartItems));
  }, [userId, cartItems]);

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}

// Replace this with your authentication logic to get the user's ID
function getAuthenticatedUserId() {
  // Example: You might get the user ID from a token or your authentication context
  return 'user123'; // Replace with the actual user ID retrieval logic
}
