import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function BurgerKing({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));

  const handlePress = () => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      navigation.navigate('BurgerKingMenu');
    });
  };

  return (
    <TouchableOpacity onPress={handlePress}>
      <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
        <View>
          <Text style={styles.brandButtonText}>Burger King</Text>
          <Text style={styles.salesQuote}>
            "Have it your way!" - Burger King
          </Text>
          {/* Additional Information */}
          <Text style={styles.additionalInfo}>
            Flame-Grilled Since 1954
          </Text>
          <Text style={styles.additionalInfo}>
            Visit us at 5678 Burger Avenue
          </Text>
        </View>
        <Icon name="arrow-right" size={30} color="#007BFF" />
      </Animated.View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  brandButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.5)',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 10,
    borderRadius: 8,
    marginBottom: 20,
    height: 150,
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF',
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  additionalInfo: {
    fontSize: 16,
    textAlign: 'center',
    marginTop: 10,
    color: '#333',
  },
});
