import React, { useState } from 'react';
import { TouchableOpacity, StyleSheet, Text, View, ScrollView, Button, Alert, ActivityIndicator } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { useCart } from '../../CartContext';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function CartIcon() {
  const navigation = useNavigation();
  const { cartItems, removeFromCart, resetCart, clearCart } = useCart();
  const [isLoading, setIsLoading] = useState(false);
  const [isPurchaseComplete, setIsPurchaseComplete] = useState(false);

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
  };

  const handleBuy = () => {
    if (cartItems.length > 0) {
      setIsLoading(true);

      // Simulate a purchase process with a delay (You can replace this with your actual purchase logic)
      setTimeout(() => {
        setIsLoading(false);
        setIsPurchaseComplete(true);
        clearCart(); // Clear the cart if the purchase is complete
      }, 2000); // Simulate a 2-second purchase process
    } else {
      // Cart is empty, show an alert
      Alert.alert('Empty Cart', 'Your cart is empty. Please add items to your cart before purchasing.');
    }
  };

  return (
    <>
      <TouchableOpacity
        onPress={() => navigation.navigate('Dashboard')}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>

      {cartItems.length > 0 ? (
        <ScrollView style={styles.cartItemsContainer}>
          <View style={styles.cartTitle}>
            <Text style={styles.cartTitleText}>CART</Text>
            <View style={styles.cartTitleLine}></View>
          </View>
          {cartItems.map((item, index) => (
            <View key={index} style={styles.cartItem}>
              <Text style={styles.cartItemName}>{item.name}</Text>
              <Text style={styles.cartItemPrice}>{item.price}</Text>
              <TouchableOpacity onPress={() => removeFromCart(item)} style={styles.removeButton}>
                <Text style={styles.removeButtonText}>Remove</Text>
              </TouchableOpacity>
            </View>
          ))}
          <Text style={styles.cartTotal}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
          {isLoading ? (
            <ActivityIndicator size="large" color="#007BFF" />
          ) : isPurchaseComplete ? (
            <Text style={styles.purchaseCompleteText}>Done!</Text>
          ) : (
            <Button title="Buy" onPress={handleBuy} />
          )}
        </ScrollView>
      ) : (
        <Text style={styles.emptyCartText}>Your cart is empty.</Text>
      )}
    </>
  );
}

const styles = StyleSheet.create({
  backButton: {
    position: 'absolute',
    backgroundColor: 'transparent',
    marginTop: 35,
    marginLeft: 10,
  },
  purchaseCompleteText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'green',
  },
  cartTotal: {
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 10,
    textAlign: 'center',
  },
  cartItemsContainer: {
    marginTop: 160,
  },
  cartTitle: {
    alignItems: 'center',
  },
  cartTitleText: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  cartTitleLine: {
    borderBottomWidth: 2,
    borderBottomColor: 'black',
    width: 1110,
    marginTop: 5,
  },
  cartItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    paddingHorizontal: 20,
    paddingVertical: 10,
    backgroundColor: '#F5F5F5',
    borderRadius: 5,
  },
  cartItemName: {
    fontSize: 20,
    flex: 1,
  },
  cartItemPrice: {
    fontSize: 20,
    fontWeight: 'bold',
    marginRight: 20,
  },
  removeButton: {
    backgroundColor: '#FF5733',
    padding: 5,
    borderRadius: 5,
  },
  removeButtonText: {
    color: 'white',
    fontSize: 12,
  },
  emptyCartText: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 180,
    textAlign: 'center',
  },
});
