import React, { createContext, useContext, useState, useEffect } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const cartStorageKey = 'userCart';

  // Load the cart items from local storage on component mount
  useEffect(() => {
    const savedCartItems = localStorage.getItem(cartStorageKey);
    if (savedCartItems) {
      setCartItems(JSON.parse(savedCartItems));
    }
  }, []);

  // Function to update the cart and local storage
  const updateCart = (newCartItems) => {
    setCartItems(newCartItems);
    localStorage.setItem(cartStorageKey, JSON.stringify(newCartItems));
  };

  const addToCart = (item) => {
    const updatedCart = [...cartItems, item];
    updateCart(updatedCart);
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name);
    updateCart(updatedCart);
  };

  const clearCart = () => {
    updateCart([]);
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
}
