import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated, TouchableWithoutFeedback } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';
import McDonaldsMenu from '../Fastfoods/McDonaldsMenu';
import BurgerKingMenu from '../Fastfoods/BurgerKingMenu';
import Subway from '../Fastfoods/Subway'; // Import the Subway component
import KFC from '../Fastfoods/KFC'; // Import the KFC component

export default function Dashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));

  const handlePress = (menu) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      navigation.navigate(menu + 'Menu');
    });
  };

  return (
    <Background>
      <Logo />
      <View style={styles.headerContainer}>
        <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
      </View>

      {/* McDonald's Section with Sales Quote */}
      <TouchableWithoutFeedback onPress={() => handlePress('McDonalds')}>
        <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
          <View>
            <Text style={styles.brandButtonText}>McDonald's</Text>
            <Text style={styles.salesQuote}>
              "I'm lovin' it!" - McDonald's
            </Text>
            {/* Additional Information */}
            <Text style={styles.additionalInfo}>
              Serving Happiness Since 1955
            </Text>
            <Text style={styles.additionalInfo}>
              Find us at 1234 Fast Food Lane
            </Text>
          </View>
          <Icon name="arrow-right" size={30} color="#007BFF" />
        </Animated.View>
      </TouchableWithoutFeedback>

      {/* Burger King Section */}
      <TouchableWithoutFeedback onPress={() => handlePress('BurgerKing')}>
        <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
          <View>
            <Text style={styles.brandButtonText}>Burger King</Text>
            <Text style={styles.salesQuote}>
              "Have it your way!" - Burger King
            </Text>
            {/* Additional Information */}
            <Text style={styles.additionalInfo}>
              Flame-Grilled Since 1954
            </Text>
            <Text style={styles.additionalInfo}>
              Visit us at 5678 Burger Avenue
            </Text>
          </View>
          <Icon name="arrow-right" size={30} color="#007BFF" />
        </Animated.View>
      </TouchableWithoutFeedback>

      {/* Subway Section */}
      <Subway navigation={navigation} />

      {/* KFC Section */}
      <KFC navigation={navigation} />

      {/* Repeating fast food sections as needed */}
      {/* ... (you can add more fast-food sections here) */}

      {/* Cart Button */}
      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>
    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
  },
  brandButton: {
    backgroundColor: 'rgba(255, 255, 255, 0.5)', // White with 50% transparency
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 10,
    borderRadius: 8,
    marginBottom: 20,
    height: 150, // Adjust the height to make boxes the same size
  },
  brandButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007BFF', // Presentable blue color
  },
  salesQuote: {
    fontSize: 18,
    fontStyle: 'italic',
    textAlign: 'center',
  },
  additionalInfo: {
    fontSize: 16,
    textAlign: 'center',
    marginTop: 10,
    color: '#333', // Change the color to your preference
  },
  cartButton: {
    backgroundColor: 'blue',
    opacity: 0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
