import React, { createContext, useContext, useState } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [carts, setCarts] = useState({}); // Use an object to store user-specific carts

  const addToCart = (userId, item) => {
    const userCart = carts[userId] || [];
    userCart.push(item);

    setCarts({ ...carts, [userId]: userCart });
  };

  const removeFromCart = (userId, item) => {
    const userCart = carts[userId] || [];
    const indexOfItemToRemove = userCart.findIndex((cartItem) => cartItem.name === item.name);

    if (indexOfItemToRemove !== -1) {
      userCart.splice(indexOfItemToRemove, 1);
      setCarts({ ...carts, [userId]: userCart });
    }
  };

  const clearCart = (userId) => {
    const updatedCarts = { ...carts };
    delete updatedCarts[userId];
    setCarts(updatedCarts);
  };

  const getCart = (userId) => {
    return carts[userId] || [];
  };

  return (
    <CartContext.Provider value={{ addToCart, removeFromCart, clearCart, getCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
