import React, { createContext, useContext, useState, useEffect } from 'react';
import { AsyncStorage } from 'react-native'; // Import AsyncStorage

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const userKey = 'authenticatedUser';

  // Load the user-specific cart from AsyncStorage when the component mounts
  useEffect(() => {
    const loadCartFromStorage = async () => {
      try {
        const user = await AsyncStorage.getItem(userKey);
        if (user) {
          const cartData = await AsyncStorage.getItem(`userCart_${user}`);
          if (cartData) {
            setCartItems(JSON.parse(cartData));
          }
        }
      } catch (error) {
        console.error('Error loading cart data:', error);
      }
    };

    loadCartFromStorage();
  }, []);

  const addToCart = (item) => {
    // Check if the item already exists in the cart
    const itemExists = cartItems.some((cartItem) => cartItem.name === item.name);

    if (!itemExists) {
      setCartItems([...cartItems, item]);

      // Save the user-specific cart to AsyncStorage when an item is added
      AsyncStorage.getItem(userKey).then((user) => {
        if (user) {
          AsyncStorage.setItem(`userCart_${user}`, JSON.stringify([...cartItems, item]));
        }
      });
    }
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name);
    setCartItems(updatedCart);

    // Save the user-specific cart to AsyncStorage when an item is removed
    AsyncStorage.getItem(userKey).then((user) => {
      if (user) {
        AsyncStorage.setItem(`userCart_${user}`, JSON.stringify(updatedCart));
      }
    });
  };

  const clearCart = () => {
    setCartItems([]);

    // Clear the user-specific cart in AsyncStorage
    AsyncStorage.getItem(userKey).then((user) => {
      if (user) {
        AsyncStorage.removeItem(`userCart_${user}`);
      }
    });
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
