import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  LinearGradient,
} from 'react-native';
import { StatusBar } from 'expo-status-bar';
import * as Animatable from 'react-native-animatable';
import Axios from 'axios';
import Logo from '../components/Logo';

const LoginScreen = ({ navigation }) => {
  const [state, setState] = useState({
    username: '',
    password: '',
    isValidUser: true,
    isValidPassword: true,
    checkTextInputChange: true,
  });

  const textUsernameChange = (val) => {
    if (val.length >= 4) {
      setState({
        ...state,
        username: val,
        checkTextInputChange: true,
      });
    } else {
      setState({
        ...state,
        username: val,
        checkTextInputChange: false,
      });
    }
  };

  const handlePasswordChange = (val) => {
    if (val.length >= 6) {
      setState({
        ...state,
        password: val,
        isValidPassword: true,
      });
    } else {
      setState({
        ...state,
        password: val,
        isValidPassword: false,
      });
    }
  };

  const handleUser = (val) => {
    if (val.trim().length >= 4) {
      setState({
        ...state,
        isValidUser: true,
      });
    } else {
      setState({
        ...state,
        isValidUser: false,
      });
    }
  };

  const LoginData = async () => {
    if (state.username.trim() === '' || state.password.trim() === '') {
      alert('Please enter both email and password.');
      return;
    }

    try {
      const response = await Axios.post('http://localhost:8000/data/signin', {
        username: state.username,
        password: state.password,
      });

      if (response.data.status === 'success') {
        navigation.push('Dashboard', { username: state.username });
      } else {
        alert('Email and password do not match.');
      }
    } catch (e) {
      alert('An error occurred during login.');
    }
  };

  return (
    <View style={styles.container}>
      <StatusBar style="auto" />
      <View style={styles.data}>
        <View style={styles.logoContainer}>
          <Logo style={styles.logo} />
        </View>
        <Text style={styles.applego}>APPLEGO</Text>
        <Text style={styles.userdata}>Username</Text>
        <TextInput
          placeholder="Enter Username"
          style={styles.username}
          onChangeText={(e) => textUsernameChange(e)}
          onEndEditing={(e) => handleUser(e.nativeEvent.text)}
        />
        {state.isValidUser ? null : (
          <Animatable.View animation="fadeInLeft" duration={500}>
            <Text style={styles.error}>Username should be 4 characters long</Text>
          </Animatable.View>
        )}

        <Text style={styles.userdata}>Password</Text>
        <TextInput
          secureTextEntry
          placeholder="Password"
          style={styles.username}
          onChangeText={(e) => handlePasswordChange(e)}
        />

        {state.isValidPassword ? null : (
          <Animatable.View animation="fadeInLeft" duration={500}>
            <Text style={styles.error}>Password should be 6 characters long</Text>
          </Animatable.View>
        )}

        <TouchableOpacity onPress={() => navigation.navigate('ResetPasswordScreen')}>
          <Text style={{ color: 'black', fontSize: 14, textAlign: 'center', alignSelf: 'center', paddingTop: 10 }}>
            Forgot Password ?
          </Text>
        </TouchableOpacity>

        <TouchableOpacity onPress={LoginData}>
              <LinearGradient colors={['#f72855', '#f72855']} style={styles.LoginUser}>
                <Text style={[styles.Button1, { fontWeight: "bold", color: "white" }]} onPress={() => navigation.navigate('Dashboard')} >Login</Text>
              </LinearGradient><Text style={styles.signup} onPress={() => navigation.navigate('RegisterScreen')}>Don't have an account? Sign Up </Text>
            </TouchableOpacity>
          </View>

      </View>

  );
}

const styles = StyleSheet.create({
  container: {
    height: "100%",
    width: "100%",
    backgroundColor: "#f55164",
  },
  data: {
    marginTop: 60,
    backgroundColor: "rgba(255, 190, 123, 0.6)", 
    height: 700,
    paddingTop: 80,
    alignContent: 'center',
    alignSelf: "center",
    width: "95%",
    borderRadius: 20,
  },
  username: {
    fontFamily: "sans-serif", 
    alignSelf: "center",
    padding: 15,
    textAlign: "center",
    borderBottomColor: "rgba(100,50,40,0.2)",
    borderBottomWidth: 1,
    width: "80%",
    backgroundColor: "#d8dbd7",
    justifyContent: "center",
    fontSize: 20,
    borderRadius: 10
  },
  Button: {
    padding: 10,
    marginTop: 20,
    backgroundColor: "lightgreen",
    textAlign: "center",
    alignSelf: "center",
  },
  logoContainer: {
    alignItems: "center",
  },
  logo: {
    width: 200, 
    height: 200, 
  },
  error: {
    color: "red",
    fontSize: 15,
    paddingLeft: 80
  },
  LoginUser: {
    justifyContent: "center",
    alignContent: 'center',
    alignSelf: "center",
    marginTop: 20,
    padding: 15,
    width: "80%",
    borderRadius: 10
  },
  Button1: {
    alignSelf: "center",
    fontSize: 20,
    justifyContent: "space-between",
  },
  userdata: {
    paddingTop: 20,
    paddingBottom: 10,
    alignSelf: "center",
    color: "#eff2e9",
    fontSize: 20,
    justifyContent: "center"
  },
  applego: {
    paddingBottom: 20,
    alignSelf: "center",
    color: "#eff2e9",
    fontSize: 40,
    fontWeight: "bold", 
    justifyContent: "center",
  },
  signup: {
    paddingTop: 10,
    color: "#eff2e9",
    fontSize: 17,
    alignSelf: "center",
  }
});
