import React, { createContext, useContext, useState, useEffect } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const user = getCurrentUser(); // Implement getCurrentUser() to get the current user (e.g., from your authentication system)

  useEffect(() => {
    if (user) {
      // Load the user-specific cart from memory on component mount
      const userCart = user.cart || [];
      setCartItems(userCart);
    }
  }, [user]);

  const addToCart = (item) => {
    if (user) {
      // Add the item to the user's cart in memory
      const updatedCart = [...cartItems, item];
      user.cart = updatedCart; // Store the cart in the user object
      setCartItems(updatedCart);
    }
  };

  const removeFromCart = (item) => {
    if (user) {
      // Remove the item from the user's cart in memory
      const updatedCart = cartItems.filter((cartItem) => cartItem.id !== item.id);
      user.cart = updatedCart; // Update the user's cart
      setCartItems(updatedCart);
    }
  };

  const clearCart = () => {
    if (user) {
      // Clear the user's cart in memory
      user.cart = [];
      setCartItems([]);
    }
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
