import React, { createContext, useContext, useState, useEffect } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const user = getCurrentUser(); // Implement getCurrentUser() to get the current user (e.g., from your authentication system)

  useEffect(() => {
    if (user) {
      // Load the user-specific cart from local storage on component mount
      const userCart = JSON.parse(localStorage.getItem(`userCart_${user.id}`));
      console.log('Loaded cart:', userCart);
      if (userCart) {
        setCartItems(userCart);
      }
    }
  }, [user]);

  const addToCart = (item) => {
    if (user) {
      // Add the item to the user's cart in local storage
      const updatedCart = [...cartItems, item];
      localStorage.setItem(`userCart_${user.id}`, JSON.stringify(updatedCart));
      console.log('Added to cart:', updatedCart);
      setCartItems(updatedCart);
    }
  };

  const removeFromCart = (item) => {
    if (user) {
      // Remove the item from the user's cart in local storage
      const updatedCart = cartItems.filter((cartItem) => cartItem.id !== item.id);
      localStorage.setItem(`userCart_${user.id}`, JSON.stringify(updatedCart));
      console.log('Removed from cart:', updatedCart);
      setCartItems(updatedCart);
    }
  };

  const clearCart = () => {
    if (user) {
      // Clear the user's cart in local storage
      localStorage.removeItem(`userCart_${user.id}`);
      console.log('Cleared cart');
      setCartItems([]);
    }
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
