import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Button from '../components/Button';
import Icon from 'react-native-vector-icons/FontAwesome'; // Import the cart icon

export default function Dashboard({ navigation }) {
  // Dummy data for international fast-food brands
  const fastFoodBrand = 'International Eats'; // Brand name
  const foodItems = [
    { name: 'Pizza', price: '$10.99' },
    { name: 'Burger', price: '$8.99' },
    { name: 'Sushi', price: '$12.99' },
    { name: 'Taco', price: '$6.99' },
    { name: 'Pad Thai', price: '$11.99' },
    { name: 'Falafel', price: '$7.99' },
    { name: 'Samosa', price: '$5.99' },
    // Add more food items as needed
  ];

  return (
    <Background>
      <View style={styles.headerContainer}>
        <Logo />
        <Header>Welcome to {fastFoodBrand}</Header>
      </View>

      <ScrollView style={styles.foodItemsContainer}>
        <Text style={styles.sectionTitle}>Menu</Text>
        {foodItems.map((item, index) => (
          <TouchableOpacity
            key={index}
            style={styles.foodItem}
            onPress={() => {
              // Handle item selection, e.g., navigate to item details screen
              navigation.navigate('FoodItemDetails', { item });
            }}
          >
            <Text style={styles.foodItemName}>{item.name}</Text>
            <Text style={styles.foodItemPrice}>{item.price}</Text>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {/* Cart icon */}
      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>

    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  foodItemsContainer: {
    flex: 1,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  cartButton: {
    backgroundColor: 'blue', 
    opacity:0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
