import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

export default function Profile({ onClose }) {
  const { username } = route.params; // Get the username passed as a parameter

  const [name, setName] = useState(''); // Default name

  useEffect(() => {
    // Assuming you have some logic to extract the name from the username
    // You can replace this with your actual logic
    const extractedName = username.split('@')[0]; // Extracts the part before @
    setName(extractedName);
  }, [username]);

  return (
    <View style={styles.container}>
      {/* Cart icon in the upper right corner */}
      <TouchableOpacity style={styles.cartButton}>
        <Text style={styles.cartIcon}>ðŸ›’</Text>
      </TouchableOpacity>

      <Text style={styles.title}>User Profile</Text>
      <View style={styles.profileBox}>
        <View style={styles.iconContainer}>
          {/* Circle icon goes here */}
          <Text style={styles.icon}>ðŸ“·</Text>
        </View>
        <Text style={styles.name}>{name}</Text>
        <Text style={styles.contactNumber}>Contact: +1234567890</Text>
      </View>
      {/* Add your profile content here */}
      <TouchableOpacity style={styles.closeButton} onPress={onClose}>
        <Text style={styles.closeButtonText}>Close</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    padding: 20,
    alignItems: 'center', // Center content horizontally
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  profileBox: {
    borderWidth: 1,
    borderColor: 'gray',
    padding: 20,
    borderRadius: 10,
    alignItems: 'center', // Center content horizontally
  },
  iconContainer: {
    backgroundColor: 'lightgray',
    width: 100,
    height: 100,
    borderRadius: 50, // To make it a circle
    justifyContent: 'center', // Center content vertically
    alignItems: 'center', // Center content horizontally
  },
  icon: {
    fontSize: 24,
  },
  name: {
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 10,
  },
  contactNumber: {
    fontSize: 16,
    marginTop: 5,
  },
  // Add styles for your profile content
  closeButton: {
    marginTop: 20,
    padding: 10,
    backgroundColor: 'blue',
    borderRadius: 5,
  },
  closeButtonText: {
    color: 'white',
    fontSize: 16,
  },
  // Cart icon styles
  cartButton: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
});
